import React, { Component } from 'react'
import Lotery from './Lotery';
import "../../Public.css";
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, Select, Input, Label, Divider } from 'semantic-ui-react';
import { NavLink, withRouter } from 'react-router-dom';

class Lotteries extends Component {
    state = {
        lotteries: {
            new: {
                header: "Lottery For Newers",
                active: false,
            },
            birthday: {
                header: "Lottery For Birthday",
                active: false,
            },
            club_members: {
                header: "Lottery For Club Members",
                active: false,
            }
        }
    }
    onClick = (x) => {
        const lotteries = { ...this.state.lotteries };
        const lotery = { ...lotteries[x.id] };
        lotery.active = !lotery.active;
        lotteries[x.id] = lotery;
        this.setState({ lotteries: lotteries })
    }
    onSubmit=()=>{
        this.props.history.replace("/")
    }
    render() {
        const array = [];
        for (let x in this.state.lotteries) {
            array.push({ id: x, config: this.state.lotteries[x] })
        }
        return (
            <div style={{ textAlign: "center" }}>
                <h1 style={{ fontSize: '20px', color: '#00b5ad' }}>| Lotteries |</h1>
                <Form>
                {array.map((x, i) => {
                    return <div>
                        <Divider></Divider>
                        <Header as='h2' color='teal' textAlign='left'>
                            <Button style={{ marginRight: "3vw", marginLeft: "1vw" }} onClick={event => this.onClick(x)}>{x.config.active ? "Delete" : "Insert"}</Button>
                            {x.config.header}
                        </Header>
                        {x.config.active == true ? <Lotery></Lotery> : ""}
                        {/* {x.id==="club_members"?<Button>Insert</Button>:""} */}
                    </div>
                })}
                <Button type="submit" onSubmit={this.onSubmit()} color='teal' style={{ marginTop: "9vh", padding: "3vh", width: "25%", backgroundColor: "#282c34" }}>Submit And Pay</Button>
                </Form>
            </div>
        )
    }
}

export default Lotteries
