const validValue = (value, validation) => {
    let valid = true;
    console.log(validation)
    if (validation.mail) {
        if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(value))
            return "Mail is not valid"
    }
    if (validation.tz) {
        if (value.length < 9)
            return "Identity must have 9 numbers"
    }
    if (validation.password) {
        if (value.length < 6)
            return "Password must have at least 6 characters"
    }
    if(validation.phone)
    {
        if(validation.length<9)
        return "Phone is not valid"
    }
    // if (tergets.minLength) {
    //     valid = value.length < tergets['maxLength'] && valid;
    // }
    return valid;
}
export default validValue;
