import React, { Component } from 'react'
import axios from 'axios';
import Store from './MyStore';
import clickNext from "../Service/ClickNext"
class MyStores extends Component {
    state = {
        stores: [],
        shown: [],
        prev:[],
        stopped: 0,
        length: "",

    }
    componentWillMount() {
        axios.get(`http://localhost:64430/api/Enterprises`).then(x => {
            this.setState({ stores: x.data, length: x.data.length });
            if (this.state.length >0) {
                var showIt = clickNext(this.state.length, this.state.stopped, this.state.stores);
                this.setState({ shown: showIt });
                var nowStopped = (this.state.stopped + this.state.shown.length);
                this.setState({ stopped: nowStopped });
                var nowLength = (this.state.length - this.state.stopped);
                this.setState({ length: nowLength });
                console.log(this.state.shown,this.state.length,this.state.stopped)
            }
        });
    }
    handelClickNext = () => {
        if (this.state.length>0) {
            var showIt = clickNext(this.state.length, this.state.stopped, this.state.stores);
            this.setState({ shown: showIt });
            var nowStopped = (this.state.stopped + this.state.shown.length);
            this.setState({ stopped: nowStopped });
            var nowLength = (this.state.length - this.state.stopped);
            this.setState({ length: nowLength });
            console.log(this.state.shown,this.state.length,this.state.stopped)
        }
    }
    handelClickPrev=()=>{
        if (this.state.stopped>0) {
            var showIt = clickNext(this.state.stopped, this.state.length, this.state.stores);
            this.setState({ shown: showIt });
            var nowStopped = (this.state.length + this.state.shown.stopped);
            this.setState({ length: nowStopped });
            var nowLength = (this.state.stopped - this.state.length);
            this.setState({ stopped: nowLength });
            console.log(this.state.shown,this.state.length,this.state.stopped)
        }
    }
    render() {
        return (
            <div>
                <h3 style={{ backgroundColor: "#00b5ad", color: "white", textAlign: "left", paddingLeft: "1%" }}>Customers</h3>
                <button disabled={this.state.length>0?false:true} onClick={this.handelClickNext}>next</button>
                <button disabled={this.state.stopped>0?false:true} onClick={this.handelClickPrev}>prev</button>
                <div>
                    {this.state.length>0?this.state.shown.map((x, i) => {
                        return <Store name={x.Name} url={x.Url} key={i}></Store>
                    }):<div>No Result</div>}
                </div>
            </div>
        )
    }
}
export default MyStores;
