import React, { Component } from 'react'
import { Button, Form, Grid, Header, Image, Loader, Dimmer, Segment, Message, Item, Label } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import axios from 'axios';
import { getEnterpriseById } from "../../../Store/actions/EnterpriseAction";
import { connect } from 'react-redux';
import "../../Public.css"
class EnterpCards extends Component {
    state = {
        loading: false,
        myCards: []
    }
    componentDidMount() {

    }
    render() {
        return (
            <div>
                <h1 style={{ fontSize: '20px', textAlign: "center", color: '#00b5ad' }}>| My Card/s |</h1>
                <Segment style={{ width: "20vw", marginLeft: "40vw", display: "inlineBlock" }}>
                    {/* <div style={{marginRight:"2vw"}}>nihjhjlk</div> */}
                    <Button 
                        content='Watch and Update'
                    // onClick={this.handleClick}
                    />
                </Segment>
                     <Button style={{marginLeft:"30vw",width:"70vw",height:"6vh"}} label="EXTRA CARD" labelPosition="left" content="Watch AND Update >>"></Button>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        enterprise: state.enterprise.enterprise,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectEnterprise: (id) => dispatch(getEnterpriseById(id)),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(EnterpCards);
