import React, { Component } from 'react'
import Lotery from './Lotery';
import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, Select, Input, Label } from 'semantic-ui-react';
import { NavLink, withRouter } from 'react-router-dom';

class Lotteries extends Component {
    state = {
        // new: false,
        // birthday: false,
        // club_members: false,
        lotteries: {
            new: {
                header: "Lottery For Newers",
                active: false,
            },
            birthday: {
                header: "Lottery For Birthday",
                active: false,
            },
            club_members: {
                header: "Lottery For Club Members",
                active: false,
            }
        }
    }
    onClick = (x) => {
        const lotteries = { ...this.state.lotteries };
        const lotery = { ...lotteries[x.id] };
        lotery.active = !lotery.active;
        lotteries[x.id]=lotery;
        this.setState({ lotteries: lotteries })
    }
    render() {
        const array = [];
        for (let x in this.state.lotteries) {
            array.push({ id: x, config: this.state.lotteries[x] })
        }
        return (
            <div>
                {array.map((x, i) => {
                    return <Segment>
                        <Header as='h2' color='teal' textAlign='center'>
                            <Button onClick={event => this.onClick(x)}>{x.config.active ? "delete" : "insert"}</Button>
                            {x.config.header}
                            {x.config.active == true ? <Lotery></Lotery> : ""}
                        </Header>
                    </Segment>
                })}
            </div>
        )
    }
}

export default Lotteries
