import React, { Component } from 'react'
import {Input,Select,Form} from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
class Lotery extends Component {
    state = {
        statusLotery: -1,
        loteryName: "",
        loteryTypes: [
            { key: 'Discount', text: 'Discount', value: 'Discount' },
            { key: "Plus Free", text: "Plus Free", value: "Plus Free" },
            { key: "X in Y", text: "X in Y", value: "X in Y" }],
        loteryType: "",
        sum: "",
        sumType: "",
        num1: "",
        num2: "",
        adding: "",
        expireDate: "",
    }
    render() {
        return (
            <div>
                <h1>
                    {this.props.name}
                </h1>
                <Form.Select
            fluid
            label='Gender'
            options={options}
            placeholder='Gender'
          />
                    action={
                        <Dropdown button basic floating options={this.state.loteryTypes} defaultValue='Discount' />}
            </div>
        )
    }
}

export default Lotery;
