import React, { Component } from 'react'
import { Icon, List, Segment } from "semantic-ui-react"
class lotery extends Component {
    state = {
        num1: null,
        num2: null,
        Addition: null,
        different: null
    }
    componentDidMount() {
        {
            this.props.Type === 1 ?
                this.setState({ different: this.props.Expiration })
                : this.props.Type === 2 ?
                    this.setState({ different: null })
                    :
                    this.setState({ different: this.props.ExpireDate })
        }
        {

            this.props.LoteryType === "Discount" ?
                this.setState({ num1: this.props.Sum, num2: this.props.SumType + " Discount" })
                : this.props.LoteryType === "Plus Free" ?
                    this.setState({ num1: this.props.Num1 + " + ", num2: this.props.Num2 + " Lottry" })
                    :
                    this.setState({ num1: this.props.Num1 + " in ", num2: this.props.Num2 + " $" })
        }
        this.setState({ Addition: this.props.Addition })
    }
    render() {
        console.log(this.state)
        console.log(this.props)
        return <Segment inverted><List divided inverted relaxed> <List.Item>
            <List.Icon size="big" name={this.props.Type === 1 ? "user plus" : this.props.Type === 2 ? "birthday" : "credit card outline"}></List.Icon>
            <List.Content>
                <List.Header>{this.props.Type === 1 ? "Lotery For Newer" : this.props.Type === 2 ? "Lotery For Birthday" : "Lotery For Club Members"}</List.Header>
              you have {this.state.num1}{this.state.num2}
            </List.Content>
        </List.Item>
        </List></Segment>

        // <div style={{height: '10vh', textAlign: "center", width: "40vw", boxShadow: '5px 2px 1px -1px rgba(0, 0, 0, 0.2), 5px 5px 5px 5px rgba(0, 0, 0, 0.14), 5px 1px 7px 4px rgba(0, 0, 0, 0.12)',marginLeft:"18vw" }}>
        //     <div style={{ color: 'rgb(0, 181, 173)', borderBottom: 'none', fontSize: '120%' }}>{this.props.Type === 1 ? "Lotery For Newer:" : this.props.Type === 2 ? "Lotery For Birthday:" : "Lotery For Club Members:"}</div>
        //     <div>you have {this.state.num1}{this.state.num2}</div>
        //     <div>{this.state.Addition != null ? "*" + this.state.Addition : ""}</div>
        // </div>

    }
}
export default lotery;
