import validForm from "./ValidStateForm";
import newPassword from "./newPassword"
const onSignUp = (form) => {
    var obj;
    if (form.newPassword === true) {
        obj=newPassword(form["Email"]);
    }
    else {
        obj = validForm(form);
    }
    for (let x in form) {
        const inputError = { ...form[x] };
        inputError.valid = true;
        form[x] = inputError;
    }
    if (obj !== null) {
        const inputError = { ...form[obj.id] };
        inputError.valid = false;
        form[obj.id] = inputError;
        return obj;
    }
    return null;
}
export default onSignUp;
