import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, FormInput, Input } from 'semantic-ui-react';
import axios from 'axios';
import validForm from '../../Service/ValidStateForm'
import { string } from 'prop-types';
class UsersSignUp extends Component {
    state = {
        form: {
            name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validation: {
                    required: true,
                    pattern: /^[a-zA-Z&.@ ]*$/,
                },
                error: "The name is incorrect",
                valid: false,
            },
            tz: {
                type: "text",
                icon: "tag",
                text: "Identity",
                value: "",
                maxLength: 9,
                validation: {
                    required: true,
                    pattern: /^[0-9]*$/,
                    tz: true
                },
                error: "The tz is incorrect",
                valid: false,
            },
            mail: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z.+*%#!@]+$/,
                    mail: true
                    // pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
                },
                error: "The email is incorrect",
                valid: false,
            },
            password: {
                type: "password",
                icon: "lock",
                text: "Password",
                value: "",
                maxLength: 15,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z]+$/,
                    password: true
                },
                error: "The password is incorrect",
                valid: false,
            },
            phone: {
                type: "text",
                icon: "phone",
                text: "Phone",
                value: "",
                maxLength: 10,
                validation: {
                    required: true,
                    pattern: /^[0-9]*$/,
                    phone: true
                },
                error: "The phone is incorrect",
                valid: false,
            },
            birthdate: {
                type: "date",
                icon: "birthday cake",
                text: "BirthDate",
                value: "",
                maxLength: 10,
                validation: {
                    required: true,
                    pattern: /^/
                },
                error: "The birthday is incorrect",
                valid: false,
            },
        },
        validForm: false,
        errorForm: "",
        trySend: false
    }
    onChangeHendler = (e, id) => {
        const form = { ...this.state.form };
        const inputChanged = { ...form[id] };
        const value = inputChanged.value;
        if (!inputChanged.validation["pattern"].test(e.target.value)) {
            console.log(inputChanged.value)
            e.target.value = inputChanged.value
        }
        else
            inputChanged.value = e.target.value;
        console.log(inputChanged)
        // this.setState({ errorForm: validForm(form) });
        // console.log(form.errorForm)
        // if (this.state.errorForm === "") {
        //     inputChanged.valid = true;
        //     this.setState({ validForm: true });
        // }
        // else
        //     inputChanged.valid = false;
        // const valid = validValue(value, inputChange.validition)
        // form[id].valid=valid;
        //     const validForm = validForm(form);
        form[id] = inputChanged;
        //     this.setState({ form: form, validForm: validForm })
        this.setState({ form: form })
        //     // //if form valid?
        // }
    }
   validValue = (value, validation,id) => {
        let valid = true;
        console.log(validation)
        if (validation.mail) {
            if (!/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(value))
                return {id:id,error: "Mail is not valid"};
        }
        if (validation.tz) {
            if (value.length < 9)
                return {id:id,error: "Identity must have 9 numbers"};
        }
        if (validation.password) {
            if (value.length < 6)
                return {id:id,error:"Password must have at least 6 characters"};
        }
        if(validation.phone)
        {
            if(validation.length<9)
            return {id:id,error:"Phone is not valid"};
        }
        // if (tergets.minLength) {
        //     valid = value.length < tergets['maxLength'] && valid;
        // }
        return null;
    }
    validForm = (form) => {
        const array = [];
        for (let x in form) {
            array.push({ id: x, config: form[x] })
        }
        array.map((element, i) => {
            const name = element.id;
            console.log(name)
            console.log(element.config.validation)
            if (element.config.validation.required) {
                console.log(element.config.validation.required)
                if (element.config.value === "") {
                    console.log(element.id)
                    return { id: element.id, error: "this " + name + " is requried" };
                }
            }
            else
                return validValue(element.config.value, element.config.validation, element.id);
        });
    }
    SignUp = () => {
        const form = { ...this.state.form };
        // form.errorForm = validForm(form, id);
        // console.log(form.errorForm)
        // if (form.errorForm === "")
        //     form[id].valid = true;
        // else
        //     form[id].valid = false;
        // form.trySend = true
        // this.setState({ form: form })
        this.setState({ trySend: true })
        var obj = this.validForm(form)
        if (obj !== null) {
            console.log(obj)
            const inputError = { ...form[obj.id] };
            // inputError.valid = false;
            inputError.valid = false;
            document.getElementById("message").innerHTML = obj.error;
            form[obj.id] = inputError;
            this.setState({ form: form })
            // this.setState({ validForm: false });
        }
        else {
            // inputError.valid = true;
            // console.log(this.state.validForm)
            axios.post(`http://localhost:64430/api/Users`, {
                Name: this.state.form["name"].value,
                Tz: this.state.form["tz"].value,
                Email: this.state.form["mail"].value,
                Password: this.state.form["password"].value,
                Phone: this.state.form["phone"].value,
                BirthDate: this.state.form["birthdate"].value,
            }).then(x => {
                this.props.history.replace('/users/login');
                console.log(x);
            }
            ).catch(x => {
                document.getElementById("message").innerHTML = x.response.data.Message;
            });
        }
    }
    _onFocus = (e) => {
        e.currentTarget.type = "date";
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        console.log(array)
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Segment stacked>
                            {array.map((x, i) => {
                                console.log(x.config)
                                return <Form.Input
                                    key={i} iconPosition="left"
                                    maxLength={x.config.maxLength}
                                    type={x.config.type}
                                    onChange={event => this.onChangeHendler(event, x.id)}
                                    className={this.state.form.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                                    // error={this.state.trySend && !x.config.valid ? x.config.error : null}
                                    placeholder={x.config.text}
                                    icon={x.config.icon}
                                >
                                </Form.Input>
                            })}
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Sign-Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
        </div>
        )

    }
}
export default UsersSignUp;
