import React, { Component } from 'react';
import { getEnterprise } from '../../Store/actions/EnterpriseAction'
import * as actionName from '../../Store/actionTypes'
import { Button, Form, Grid, Header, Image, Message, Segment, Loader, Dimmer } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import '../../App.css'
import { NavLink, withRouter, useLocation, Prompt } from 'react-router-dom';
import axios from 'axios';
import { connect } from 'react-redux'
class Login extends Component {
  state = {
    form: {
      mail: {
        value: "",
        text: "Email",
        validation: {
          pattern: /^[0-9a-zA-Z.+*%#!@]+$/,
          required: true,
          mail: true,
        }
      },
      password: {
        value: "",
        text: "Password",
        validation: {
          pattern: /^[0-9a-zA-Z]+$/,
          required: true,
          password: true,
        }
      }
    },
    trySend: "",
    loading: "",
    errorMessage: "",
    permission: true,
    timeIsHoverError: "",
  }
  getUser = () => {
    axios.get(`http://localhost:64430/api/users?password=${this.state.password}&email=${this.state.mail}`).then(x => {
      this.props.selectUser(x.data);
      this.props.history.replace('/users/cards');
      console.log(this.props.user);
    }
    ).catch(x => { document.getElementById("login_message").innerHTML = x.response.data.Message; }
    );
  }
  getEnterprise = () => {
    axios.get(`http://localhost:64430/api/Enterprises?password=${this.state.password}&email=${this.state.mail}`).then(x => {
      this.props.selectEnterprise(x.data);
      console.log(this.props.enterprise);
      this.props.history.replace("/enterprises/new");
    }).catch(x => document.getElementById("login_message").innerHTML = x.response.data.Message);
  }
  newPassword = () => {
    if (this.mailValidation() === true)
      if (this.props.location.pathname === '/users/login') {
        document.getElementById("dimmer").setAttribute("active", "true");
        axios.get(`http://localhost:64430/api/Users?email=${this.state.mail}`).then(x => {
          console.log(x.data);
          this.props.selectUser(x.data)
          console.log(this.props.user);
          document.getElementById("newPassword").innerHTML = "sending email to:  " + x.data.Email;
        }).catch(x => document.getElementById("newPassword").innerHTML = x.response.data.Message);
      }
      else {
        const status = false;
        axios.get(`http://localhost:64430/api/Enterprises?email=${this.state.mail} `).then(x => {
          console.log(x.data);
          this.props.selectEnterprise(x.data);
          document.getElementById("newPassword").innerHTML = "sending email to:  " + x.data.Email;
        }).catch(x => document.getElementById("newPassword").innerHTML = x.response.data.Message);
      }
  }
  mailValidation = () => {
    if (this.state.mail === "") {
      document.getElementById("login_message").innerHTML = "mail is required";
      document.getElementsByClassName("mail")[0].classList.add("error");
      return false;
    }
    else
      if (!this.state.mail.includes("@")) {
        document.getElementById("login_message").innerHTML = "mail must includes @"
        document.getElementsByClassName("mail")[0].classList.add("error");
        return false
      }
    return true;
  }
  passwordValidation = () => {
    if (this.state.password === "") {
      document.getElementById("login_message").innerHTML = "password is required";
      document.getElementsByClassName("password")[0].classList.add("error");
      return false;
    }
    return true;
  }
  handleSubmit = (e) => {
    document.getElementById("login_message").innerHTML = "";
    if (this.mailValidation() === true && this.passwordValidation() === true) {
      if (this.props.location.pathname === '/users/login')
        this.getUser();
      else
        this.props.selectEnterprise(this.state.mail, this.state.password);
    }
  }
  handleOnChangeMail = (event) => {
    this.setState({ mail: event.target.value });
    document.getElementsByClassName("mail")[0].classList.remove("error");
  }
  handleOnChangePassword = (event) => {
    this.setState({ password: event.target.value });
    document.getElementsByClassName("password")[0].classList.remove("error");
  }
  render() {
    const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
    return (
      <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
        <Grid.Column style={{ maxWidth: 450 }}>
          <Header as='h2' color='teal' textAlign='center'>
            <Image src='/Images/logo.png' /> Log-in to your account
        </Header>
          <Form size='tiny' onSubmit={this.handleSubmit}>
            <Segment stacked>
              {array.map((x, i) => {
                return <Form.Input
                  key={i} iconPosition="left"
                  type="password"
                  onChange={event => this.onChangeHendler(event, x.id)}
                  className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                  placeholder={x.config.text + "*"}
                  icon="lock">
                </Form.Input>
              })}
              <div id="login_message" style={{ color: "red" }}>{this.props.error}</div>
              <Button type="submit" color='teal' fluid size='large'>
                Login
            </Button>
              <div id="email_message" style={{ display: 'none' }}>sending email</div>
            </Segment>
          </Form>
          <Message>
            New to us?<NavLink to={this.props.location.pathname === '/users/login' ? '/users/signUp' : '/enterprises/signUp'} >Sign Up</NavLink>&nbsp;
      </Message>
          <Message>
            <label>forget your password?<label onClick={this.newPassword} style={{ color: 'blue' }}>new password</label></label>
          </Message>
          <Dimmer id='dimmer' inverted='false'>
            <Loader size='mini'>Loading</Loader>
          </Dimmer>
          <div id="newPassword"></div>
        </Grid.Column>
        {this.props.loading ? <loading /> : null}
      </Grid>
    )
  }
}
const mapStateToProps = state => {
  return {
    user: state.user.user,
    enterprise: state.enterprise.enterprise
  }
}
const mapDispatchToProps = dispatch => {
  return {
    selectUser: (user) => dispatch({ type: "SELECT_USER", payload: user }),
    selectEnterprise: (enterprise) => dispatch({ type: actionName.selectEnterprise, payload: enterprise }),
    // selectEnterprise: (email,password) => dispatch(getEnterprise(email,password)),
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(Login);
