import validator from 'validator';
const newPassword = (mail) => {
    var obj;
    if (mail.value === "") {
        obj={ id: "Email", error: "mail is requried" };
    }
    if (!validator.isEmail(mail.value))
        obj={ id: "Email", error: "mail is not valid" };
    // for (let x in form) {
    //     const inputError = { ...form[x] };
    //     inputError.valid = true;
    //     form[x] = inputError;
    // }
    if (obj !== null) {
        const inputError = { ...form[obj.id] };
        inputError.valid = false;
        form[obj.id] = inputError;
        return obj;
    }
    return null;
}
export default newPassword;
