import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Input, Select, Form, Dropdown, Grid, Segment, Radio, Header, Image, TextArea, Popup, Button } from 'semantic-ui-react';
class Lotery extends Component {
    state = {
        statusLotery: -1,
        loteryName: "",
        loteryTypes: [
            { key: 'Discount', text: 'Discount', value: 'Discount' },
            { key: "Plus Free", text: "Plus Free", value: "Plus Free" },
            { key: "X in Y", text: "X in Y", value: "X in Y" }],
        sumTypes: [
            { key: '%', text: '%', value: '%' },
            { key: '$', text: '$', value: '$' }
        ],
        form: {
            loteryType: "Discount",
            sum: "",
            sumType: "",
            num1: "",
            num2: "",
            addition: "",
            expireDate: "",
        },

    }
    hanleSelctOnChange = (event, value) => {
        const form = { ...this.state.form }
        form.loteryType = value;
        form.expireDate = "";
        // this.setState({ loteryType: value });
        this.setState({ form: form });


    }
    render() {
        return (
            <div>
                <Segment>
                    <Form size="large">
                        <Form.Group widths={6}>
                            <Popup content="Select Your Lotery Type" trigger={
                                <Form.Field
                                    control={Select}
                                    options={this.state.loteryTypes}
                                    defaultValue="Discount"
                                    onChange={(event, { value }) => this.hanleSelctOnChange(event, value)}
                                />
                            }
                            />
                        </Form.Group>
                        <Form.Group inline widths={12}>
                            {this.state.form.loteryType === "Discount" ?
                                <Form.Field>
                                    <Input
                                        type="number"
                                        labelPosition='right'
                                        label={<Dropdown options={this.state.sumTypes} defaultValue={"$"} />}
                                    /></Form.Field>
                                :
                                this.state.form.loteryType === "Plus Free" ?
                                    <Form.Field inline>
                                        <Input
                                            type="number"
                                        /> + <Input
                                            type="number"
                                        />
                                    </Form.Field> :
                                    <Form.Field inline>
                                        <Input
                                            type="number"
                                        /> In <Input
                                            type="number"
                                        />
                                    </Form.Field>}
                        </Form.Group>
                        <Form.Group widths={6}>
                            <Form.Field
                                control={Input}
                                type="date"
                                value={this.state.form.expireDate}
                                placeholder="expireDate"
                                onChange={(e, { value }) => {
                                    const form = { ...this.state.form }
                                    form["expireDate"] = value
                                    this.setState({ form: form })
                                }}
                            />
                            <Form.Field
                                control={Input}
                                type="text"
                                placeholder="Addition"
                                maxLength={50}
                            /></Form.Group>
                    </Form>
                </Segment>
            </div >
        )
    }
}

export default Lotery;
