import React, { Component, createRef } from "react";
import { NavLink, Link } from 'react-router-dom';
import {
    Button,
    Header,
    Icon,
    Image,
    Menu,
    Segment,
    Sidebar, Sticky, Ref
} from "semantic-ui-react";
import Login from '../Login/Login';
import { Route, Switch, Redirect, Router } from 'react-router-dom';
import UsersSignUp from '../Users/SignUp/SignUp';
import UserApplication from '../Users/Application/Application';
import Stores from '../MyStores/MyStores';
import Report from '../Users/Report/Report';
import UserNewCard from '../Users/NewCard/NewCard';
import EnterpApplication from '../Enterprise/Application/Application';
import EnterpNewCard from '../Enterprise/NewCard/NewCard';
import Update from '../Enterprise/Update/Update';
import Cards from '../Users/Cards/Cards';
import NewCard from '../Users/NewCard/NewCard';
import { withRouter } from 'react-router-dom'
import Agreement from '../Enterprise/Agreement/Agreement';
import Clearing from '../Clearing/Clearing';
import EnterpriseSingUp from '../Enterprise/SignUp//SignUp';
import NewPassword from '../NewPassword/NewPassword';
import ContactUs from '../ContactUs/ContactUs';
import Chat from '../Chat/Chat';
import Lotery from '../Enterprise/Lotery/Lotery';
import Home from "../Home/Home";
class MySidebar extends Component {
    state = {
        visible: false,
        navlinks: {
            1: {
                value: "Home",
                path: "/",
            },
            2: {
                value: "User login",
                path: "users/login"
            },
            3: {
                value: "Enterprise login",
                path: "enterprises/login"
            },
            4: {
                value: "About",
                path: "/about"
            },
            5: {
                value: "Why it's worth me",
                path: "/"
            },
            6: {
                value: "Why it's worth my enterprise",
                path: "/"
            },
            7: {
                value: "Enterprises",
                path: "/"
            },

            8: {
                value: "Contact us",
                path: "contect"
            },
        }
    };
    handleHideClick = () => this.setState({ visible: false });
    handleShowClick = () => this.setState({ visible: true });
    handleSidebarHide = () => this.setState({ visible: false });

    render() {
        const contextRef = createRef();
        const array = [];
        for (let x in this.state.navlinks) {
            array.push({ id: x, config: this.state.navlinks[x] })
        }
        const { visible } = this.state;
        return (
            <div>
                <Ref innerRef={contextRef}>
                    <Sidebar.Pushable style={{ transform: "none" }}>
                        <Sticky context={contextRef}>
                            <div style={{ backgroundColor: "red", textAlign: "left", zIndex: "50",height:"7vh" }} >
                                <Button id="sidebarout" onClick={this.handleShowClick}>|||</Button></div>
                            <Sidebar style={{ backgroundColor: "#282c34" }}
                                as={Menu}
                                animation="overlay"
                                icon="labeled"
                                inverted
                                onHide={this.handleSidebarHide}
                                vertical
                                visible={visible}
                                width="thin" >
                                <Button id="sidebarin" onClick={this.handleHideClick}>X</Button>
                                {array.map((x, i) => {
                                    return <Menu.Item as='a'><NavLink to={x.config.path}>{x.config.value}</NavLink></Menu.Item>
                                })}
                            </Sidebar>
                        </Sticky>
                        <Sidebar.Pusher>
                        <Switch>
                            <Route path="/" component={Home} />
                            <Route path="/users/login" component={Login} />
                            <Route path="/enterprises/login" component={Login} />
                        </Switch>
                        </Sidebar.Pusher>
                    </Sidebar.Pushable>
                </Ref>
            </div>
        );
    }
}

export default MySidebar
