import React, { Component } from 'react';
import { NavLink } from "react-router-dom";
import axios from 'axios';
import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, LabelDetail } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
class EnterpriseSignUp extends Component {
    state = {
        Input: [
            { name: 'Name', icon: 'user', placeholder: 'Name', type: 'text', onChange: 'handleChangeName',value:''},
            { name: 'Email', icon: 'envelope', placeholder: "E-mail address", type: "email",value:'' },
            { name: 'Code', icon: "lock", placeholder: "Password", type: "password", onChange: 'handleChangeCode',value:'' },
            { name: 'Phone', icon: "phone", placeholder: "Phone", type: "tel", onChange: 'handleChangePhone',value:'' },
            { name: 'Url', icon: "paper plane", placeholder: "Url", type: "text",value:'' },
        ],
        agreement: <a href="/agreement" target="_blank">Service</a>
    }
    postSignUp = () => {
        const enterprise = {};
        this.state.Input.map(x => {
            enterprise[x.name] = document.getElementsByName(x.name)[0].value;
        })
        console.log(enterprise)
        axios.post(`http://localhost:64430/api/Enterprises`, enterprise).then(x => {
            this.props.history.replace('/enterprises/login');
            console.log(x);
        }
        ).catch(x => {
            document.getElementById("message").innerHTML = x.response.data.Message;
        });
    }
    handleChangeName = (e) => {
        if (e.target.value.match("^[a-zA-Z ]*$") != null)
        this.setState.Input({[0]:e.target.value})
    }
    render() {

        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form onSubmit={this.postSignUp} size='large'>
                        <Segment stacked>
                            {this.state.Input.map((x, i) => {
                                return <Form.Input
                                    name={x.name}
                                    key={i}
                                    icon={x.icon}
                                    iconPosition="left"
                                    placeholder={x.placeholder}
                                    required={true}
                                    type={x.type}
                                    onChange={x.onChange}
                                    value={x.value}
                                >
                                </Form.Input>
                            })}
                            <input type="Checkbox" required></input>
                            <label>I agree to the Terms of {this.state.agreement}</label>
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Sign Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
        </div>
        )
    }
}
export default EnterpriseSignUp;
