import React, { Component } from 'react';
import { NavLink } from "react-router-dom";
import axios from 'axios';
import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, LabelDetail } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
class EnterpriseSignUp extends Component {
    state = {
        form: {
            name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validition: {
                    required: true,
                    pattern: "^[a-zA-Z ]*$"
                },
                error: "The name is incorrect",
                valid: false,
                toched: false
            },
            mail: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validition: {
                    required: true,
                    pattern: "/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/^[0-9]*$"
                },
                error: "The email is incorrect",
                valid: false,
                toched: false
            },
            code: {
                type: "Password",
                icon: "lock",
                text: "Password",
                value: "",
                maxLength: 15,
                validition: {
                    required: true,

                    // pattern:"^[0-9]*$"
                },
                error: "The password is incorrect",
                valid: false,
                toched: false
            },
            phone: {
                type: "text",
                icon: "phone",
                text: "Password",
                value: "",
                maxLength: 10,
                validition: {
                    required: true,

                    pattern: "^[0-9]*$"
                },
                error: "The phone is incorrect",
                valid: false,
                toched: false
            },
            //     <Form.Input
            //     Name='Url' icon="paper plane" iconPosition="left" placeholder='Url' required={true} type='text' maxLength={100} onChange={(event) => this.setState({ url: event.target.value })} value={this.state.url}>
            //  </Form.Input>
            url: {
                type: "text",
                icon: "paper",
                text: "Url",
                value: "",
                maxLength: 100,
                validition: {
                    required: true,
                },
                error: "The url is incorrect",
                valid: false,
                toched: false
            },
        },
        agreement: <a href="/agreement" target="_blank">Service</a>
    }
    // SignUp = () => {
    //     const enterprise = {};
    //     enterprise["Name"] = document.getElementsByName("Name")[0].value;
    //     enterprise["Email"] = document.getElementsByName("Email")[0].value;
    //     enterprise["Code"] = document.getElementsByName("Code")[0].value;
    //     enterprise["Password"] = document.getElementsByName("Password")[0].value;
    //     enterprise["Url"] = document.getElementsByName("Url")[0].value;
    //     console.log(enterprise)
    //     axios.post(`http://localhost:64430/api/Enterprises`, enterprise).then(x => {
    //         this.props.history.replace('/enterprises/login');
    //         console.log(x);
    //     }
    //     ).catch(x => {
    //         document.getElementById("message").innerHTML = x.response.data.Message;
    //     });
    // }
    onChangeHendler = (e, id) => {
        const form = { ...this.state.form };
        const inputChanged = { ...form[id] };
        const value = inputChanged.value;
        // if (!inputChanged.validation["pattern"].test(e.target.value)) {
        //     console.log(inputChanged.value)
        //     e.target.value = inputChanged.value
        // }
        // else
        inputChanged.value = e.target.value;
        console.log(inputChanged)
        form[id] = inputChanged;
        this.setState({ form: form })
    }
    SignUp = () => {
        axios.post(`http://localhost:64430/api/Enterprises`, {
            Name: this.state.form["name"].value,
            Email: this.state.form["mail"].value,
            Code: this.state.form["code"].value,
            Phone: this.state.form["phone"].value,
            BirthDate: this.state.form["birthdate"].value,
        })
            .then(x => {
                this.props.history.replace('/users/login');
                console.log(x);
            }
            ).catch(x => {
                document.getElementById("message").innerHTML = x.response.data.Message;
            });
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form onSubmit={this.SignUp} size='large'>
                        {array.map((x, i) => {
                            return <Form.Input onChange={event => this.onChangeHendler(event, x.id)} key={i} iconPosition="left" maxLength={x.config.maxLength} type={x.config.type} placeholder={x.config.text} icon={x.config.icon}></Form.Input>
                        })}
                        <Segment stacked>
                            <input type="Checkbox" required></input>
                            <label>I agree to the Terms of {this.state.agreement}</label>
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Sign Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
        </div >
        )
    }
}
export default EnterpriseSignUp;
