import React, { Component } from 'react'

class Header extends Component {
        componentDidUpdate() {
        switch (this.state.routeIn) {
            case false: {
                if (this.props.history.location.pathname !== "/")
                    this.setState({ routeIn: true });
                break;
            }
            case true: {
                if (this.props.history.location.pathname === "/")
                    this.setState({ routeIn: false });
                break;
            }
            default: break;
        }
        console.log(this.props)
        if (this.props.user !== null)
            this.navlinks = [
                { value: "My Cards", path: "/users/cards" },
                { value: "New Cards", path: "/users/new" },
                { value: "Update Profile", path: "/users/ProfileUpdate" },
                { value: "Discontact", path: "" }
            ]
        else
            if (this.props.enterprise !== null) {
                this.navlinks = [
                    { value: "My Cards", path: "/enterprises/enterpCards" },
                    { value: "Update Profile", path: "/enterprises/ProfileUpdate" },
                    { value: "New Card", path: "/enterprises/newCard" },
                    { value: "Agreement and price list", path: "/agreement" },
                    { value: "Discontact", path: "" }]
            }
            else
                if (this.props.history.location.pathname === "/") {
                    this.navlinks = [{ value: "Home", path: "" },
                    { value: "About", path: "about" },
                    { value: "Why it's worth me", path: "about_user" },
                    { value: "Why it's worth my enterprise", path: "about_enterprise" },
                    { value: "Enterprises", path: "stores" },
                    { value: "Contact us", path: "contact" }]
                }
                else {
                    this.navlinks = [{ value: "Home", path: "/" }]
                }
    }
  render() {
    return (
 <div>header</div>
    )
  }
}

export default Header
