import React, { Component } from 'react'
import { Button, Form, Grid, Header, Image, Message, Segment, Step, Group } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
import { NavLink, withRouter, useLocation, Prompt } from 'react-router-dom';
import axios from 'axios';
import { connect } from 'react-redux'
import moment from 'moment';

class newPassword extends Component {
    state = {
        password: "",
        confirm_password: "",
    }
    componentWillMount() {
        let search = this.props.location.search;
        console.log(search);
        let params = new URLSearchParams(search);
        let date = params.get('date');
        console.log(date);
        this.props.selectSendDate(date);
        // console.log(moment(date.tostring()).format("dd.mm.yyyy hh:MM:ss"));
        // const today = new Date(date.toString());
        // console.log(new Intl.DateTimeFormat('en-US', {year: 'numeric', month: '2-digit',day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'}).format(today));
    }
    changePassword = () => {
        document.getElementById("message").innerHTML = "";
        if (this.state.password === "") {
            document.getElementsByClassName("password")[0].classList.add("error");
            document.getElementById("message").innerHTML = "password is required"
        }
        else {
            if (this.state.confirm_password === "") {
                document.getElementsByClassName("confirm_password")[0].classList.add("error");
                document.getElementById("message").innerHTML = "confirm_password is required"
            }
            else {
                if (this.state.confirm_password !== this.state.password) {
                    document.getElementsByClassName("confirm_password")[0].classList.add("error");
                    document.getElementById("message").innerHTML = "confirm password is incorrect";
                }
                else {
                    let search = this.props.location.search;
                    console.log(search);
                    let params = new URLSearchParams(search);
                    console.log(params);
                    let key = params.get('key');
                    let date = params.get('date');
                    if (this.props.location.pathname == '/users/newpassword') {
                        axios.post(`http://localhost:64430/api/Users?key=${key}&password=${this.state.password}`).then(x => {
                            console.log(x.data);
                            document.getElementById("message").innerHTML = "the password was changed successfully";
                        }).catch(x => document.getElementById("message").innerHTML = x.response.data.Message);
                    }
                    else {
                        axios.post(`http://localhost:64430/api/Enterprises?key=${key}&password=${this.state.password}`).then(x => {
                            console.log(x.data);
                            document.getElementById("message").innerHTML = x.data;
                            document.getElementById("message").innerHTML = "the password was changed successfully";
                        }).catch(x => document.getElementById("message").innerHTML = x.response.data.Message);
                    }
                }
            }
        }
    }
    handleChangePassword = (e) => {
        this.setState({ password: e.target.value })
        document.getElementsByClassName("password")[0].classList.remove("error");
        document.getElementsByClassName("confirm_password")[0].classList.remove("error");
    }
    handleChangeConfirmPassword = (e) => {
        this.setState({ confirm_password: e.target.value })
        document.getElementsByClassName("confirm_password")[0].classList.remove("error");
        document.getElementsByClassName("password")[0].classList.remove("error");
    }
    render() {
        const StepExampleCompleted = () => (
            <Step.Group>
                <Step completed>
                    <Icon name='credit card' />
                    <Step.Content>
                        <Step.Title>Billing</Step.Title>
                        <Step.Description>Enter billing information</Step.Description>
                    </Step.Content>
                </Step>
            </Step.Group>
        )
        return (
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> New Password</Header>
                    <Form size='tiny' onSubmit={this.changePassword}>
                        <Segment stacked>
                            <Form.Input className="password"
                                fluid icon='lock'
                                iconPosition='left'
                                placeholder='password'
                                type='password'
                                onChange={this.handleChangePassword}
                            />
                            <Form.Input className="confirm_password"
                                fluid
                                icon='lock'
                                iconPosition='left'
                                placeholder='confirm password'
                                type='password'
                                onChange={this.handleChangeConfirmPassword}
                            // className={!value.valid?'error':null}
                            />
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large' >
                                Change Password
  </Button>

                        </Segment>
                    </Form>
                </Grid.Column>
                <Step ></Step>
            </Grid>

        )
    }
}
const mapStateToProps = state => {
    return {
        user: state.user.user,
        enterprise: state.enterprise.enterprise,
        date: state.user.user.SendDate
    }
}
const mapDispatchToProps = dispatch => {
    return {
        selectSendDate: (date) => dispatch({ type: "SELECT_SEND_DATE", payload: date }),
        selectUser: (user) => dispatch({ type: "SELECT_USER", payload: user }),
        selectEnterprise: (enterprise) => dispatch({ type: "SELECT_ENTERPRISE", payload: enterprise }),
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(newPassword);
