import React, { Component } from 'react';
import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, Select } from 'semantic-ui-react';
import { NavLink, withRouter } from 'react-router-dom';
import { InputFile } from 'semantic-ui-react-input-file';
class NewCard extends Component {

    state = {
        Input: [
            { name: 'password', icon: "address card", placeholder: "Password", type: "text" },
            { name: 'mail', icon: "envelope", placeholder: "Email", type: "text" },
            { name: 'cost', icon: 'dollar sign', placeholder: 'Card Price', type: 'text' },
            // { name: 'Count_points', icon: 'envelope', placeholder: "Count points", type: "email" },         
            { name: 'img', icon: "images outline", placeholder: "Img", type: "file" },
            { name: 'new', icon: "circle", placeholder: "Prise for new", type: "Checkbox" },
            { name: 'birthday', icon: "birthday cake", placeholder: "Prise for birthday", type: "Checkbox" },
            { name: 'type', icon: "paper plane", placeholder: "Type", type: "text" },
        ],
        status_new: false,
        status_birthday: false,
    }
    genderOptions = [
        { key: '%', text: '%', value: '%' },
        { key: 'Points', text: 'points', value: 'Points' },
    ]
    render() {
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' />New card!!!
                  </Header>
                    <Form size='large'>
                        <Segment stacked>
                            {this.state.Input.map((x, i) => {
                                return <Form.Input
                                    name={x.name}
                                    key={i}
                                    icon={x.icon}
                                    iconPosition="left"
                                    placeholder={x.placeholder}
                                    required={true}
                                    type={x.type}
                                >
                                </Form.Input>
                            })}
                            <Form.Field
                                // icon='envelope'
                                control={Select}
                                options={this.genderOptions}
                                placeholder='Count points'
                                searchInput={{ id: 'form-select-control-gender' }}
                            />
                            <label icon="birthday cake">Prise for birthday</label>
                            <Form.Field
                                label='30%'
                                control='input'
                                type='radio'
                                name='htmlRadios'
                            />
                            <Form.Field
                                label='20 points'
                                control='input'
                                type='radio'
                                name='htmlRadios'
                            />
                            <InputFile class=".ui.form .field .ui.input" style={{ width: '100%' }}
                                // button={{ ...buttonProps }}
                                input={{
                                    id: 'input-form-id',
                                    // onChange: handleUpload
                                    style:{width:"100%"}
                                }}
                            />
                            <label for="file" class="ui icon button">
                                <i class="file icon"></i>
                                Open File</label>
                            <input type="file" id="file" style={{display:'none'}} />
                            <Button color='teal' fluid size='large'>
                                buy new cards
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
        </div>
        )
    }
}
export default NewCard;
