import React, { Component } from 'react'
import axios from 'axios';
import Store from './MyStore';
import seeMore from "../Service/SeeMore"
class MyStores extends Component {
    state = {
        stores: [],
        shown: [],
        stoppped: 0,
        length: "",

    }
    componentDidMount() {
        axios.get(`http://localhost:64430/api/Enterprises`).then(x => {
            this.setState({ stores: x.data, length: x.data.length });
            if (this.state.length >= 3) {
                var showIt = [];
                var i;
                for (i = 0; i < 3; i++) {
                    showIt[i] = this.state.stores[i + this.state.stoppped];
                }
                this.setState({ stoppped: this.state.stoppped + 3, shown: showIt });
            }
        });
    }
    handelClick = () => {
        // if (this.state.length >= 3) {
        //     = [];
        //     var i;
        //     for (i = 0; i < 3; i++) {
        //         showIt[i] = this.state.stores[i + this.state.stoppped];
        //     }
        // }
        // else {
        //     for (i = this.state.stoppped; i < this.state.length; i++) {
        //         showIt[i - this.state.stoppped] = this.state.stores[i + this.state.stoppped];
        //     }
        // }
        var showIt=seeMore(this.state.length,this.state.stoppped,this.state.stores)
        this.setState({ stoppped: this.state.stoppped + 3, shown: showIt });
    }
    render() {
        return (
            <div>
                <h3 style={{ backgroundColor: "#00b5ad", color: "white", textAlign: "left", paddingLeft: "1%" }}>Customers</h3>
                <div onClick={this.handelClick}>see more</div>
                <div>
                    {this.state.shown.map((x, i) => {
                        return <Store name={x.Name} url={x.Url} key={i}></Store>
                    })}
                </div>
            </div>
        )
    }
}
export default MyStores;
