import * as actionName from './actionTypes'
const initalState = {
    enterprise: null,
    loading: false,
    error: "",
    message: null,
    card: null,
    submitNewCard: false,
    lottery:[]
}
const rootReducer = (state = initalState, action) => {
    switch (action.type) {
        case actionName.selectEnterprise:
            return {
                ...state,
                enterprise: action.payload
            }
        case actionName.error:
            return {
                ...state,
                error: action.payload
            }
        case actionName.loading:
            return {
                ...state,
                loading: action.payload
            }
        case actionName.message:
            return {
                ...state,
                message: action.payload
            }
        case actionName.cleanEnterpriseState:
            return {
                ...state,
                enterprise: null,
            }
        case actionName.selectCard:
            return {
                ...state,
                card: action.payload
            }
        case actionName.submitNewCard:
            return {
                ...state,
                submitNewCard: action.payload
            }
        case actionName.selectLottery:
            return {
                ...state,
                lottery:action.payload,
            }
        // case actionName.selectLotteryForBirthDay:
        //     return {
        //         ...state,
        //         lotteryForBirthDay: action.payload
        //     }
        // case actionName.selectLotteryForClubMembers:
        //     return {
        //         ...state,
        //         lotteryForClubMembers: action.payload
        //     }
        default: return state
    }
}
export default rootReducer;
