import React, { Component } from 'react'
import axios from 'axios';
import { connect } from 'react-redux'
import Card from './Card';
import Lotery from './Lotery';
class Cards extends Component {
    state = {
        cards: "",
        loading: true
    }
    componentDidMount() {
        this.setState({ loading: true });
        console.log(this.props.user.C_id);
        axios.get(`http://localhost:64430/api/ClubCards?userId=${this.props.user.C_id}`).then(res => {
            this.setState({ cards: res.data })
            console.log(this.state.cards);
            this.setState({ loading: false });

        }).catch(x => { 
            this.setState({ loading: false });
            console.log(x.response.data.Message) })
    }
    render() {
        let card = <div>loading...</div>;
        if (this.state.loading === false) {
            console.log(this.state.cards);
            card = this.state.cards.map((x, i) => {
                return <div>
                    <Card name={x.Name}
                        key={i}
                        expire={x.ExpireDate}
                        src={x.Img}
                        type={x.Type}
                        url={x.Url}
                        points={x.Points}></Card>
                    <Lotery sum={x.Sum}
                    sumType={x.SumType}
                    num1={x.Num1}
                    num2={x.Num2}
                    addition={x.Addition}
                    >
                    </Lotery>
                    <Lotery sum={x.Sum1}
                    sumType={x.SumType1}
                    num1={x.Num11}
                    num2={x.Num21}
                    addition={x.Addition1}>
                    </Lotery>
                    <Lotery sum={x.Sum2}
                    sumType={x.SumType2}
                    num1={x.Num12}
                    num2={x.Num22}
                    addition={x.Addition2}> 
                    </Lotery>
                    <Lotery sum={x.Sum3}
                    sumType={x.SumType3}
                    num1={x.Num13}
                    num2={x.Num23}
                    addition={x.Addition3}>
                    </Lotery>
                </div>
            })
        }
        return (
            <div>
                <h1>{card}</h1>

            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        user: state.user.user
    }
}
export default connect(mapStateToProps, null)(Cards);
