import * as actionName from './actionTypes'
const initalState = {
    enterprise: null,
    loading: false,
    error: "",
    message: null,
    card: null,
    submitNewCard: false,
    loteryForNewers:false,
    loteryForBirthDay:false,
    loteryForClabMembers:false
}
const rootReducer = (state = initalState, action) => {
    switch (action.type) {
        case actionName.selectEnterprise:
            return {
                ...state,
                enterprise: action.payload
            }
        case actionName.error:
            return {
                ...state,
                error: action.payload
            }
        case actionName.loading:
            return {
                ...state,
                loading: action.payload
            }
        case actionName.message:
            return {
                ...state,
                message: action.payload
            }
        case actionName.cleanEnterpriseState:
            return {
                ...state,
                enterprise: null,
            }
        case actionName.selectCard:
            return {
                ...state,
                card: action.payload
            }
        case actionName.submitNewCard:
            return {
                ...state,
                submitNewCard: action.payload
            }
        default: return state
    }
}
export default rootReducer;
