import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image,Segment, FormInput} from 'semantic-ui-react';
import axios from 'axios';
class UsersSignUp extends Component {
    state = {
        Input: [
            { name: 'Name', icon: 'user', placeholder: 'Name', type: 'text' },
            { name: 'tz', icon: "tag", placeholder: "Identity", type: "text" },
            { name: 'Email', icon: 'envelope', placeholder: "E-mail address", type: "email" },
            { name: 'Password', icon: "lock", placeholder: "Password", type: "password" },
            { name: 'Phone', icon: "phone", placeholder: "Phone", type: "tel", required: "required" },
            { name: 'BirthDate', icon: "birthday cake", placeholder: "BirthDate", type: "date", },
        ]
    }
    SignUp = (e) => {
        e.preventDefault();
        const user = {};
        this.state.Input.map(x => {
            user[x.name] = document.getElementsByName(x.name)[0].value;
        })
        user['Level'] = 0;
        console.log(user)
        axios.post(`http://localhost:64430/api/users`, user).then(x => {
            // this.setState({ user: x.user })
            // console.log(this.state.user);
            console.log(x.data);
            this.props.history.replace('/users/login');
        }
        ).catch(x => {
            document.getElementById("message").innerHTML=x.response.data.Message;
        });

    }
    render() {
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Segment stacked>
                            {this.state.Input.map((x, i) => {
                                return <Form.Input
                                    name={x.name}
                                    key={i}
                                    icon={x.icon}
                                    iconPosition="left"
                                    placeholder={x.placeholder}
                                    required={true}
                                    type={x.type}
                                >
                                </Form.Input>
                            })}
                            
                            <div id="message" style={{color:"red"}}></div>
                            <Button  type="submit" color='teal' fluid size='large'>
                                Sign-Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
        </div>
        )
    }
}
export default UsersSignUp;
