import React, { Component } from 'react';
import { NavLink } from "react-router-dom";
import axios from 'axios';
import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, LabelDetail } from 'semantic-ui-react';
import 'semantic-ui-css/semantic.min.css';
class EnterpriseSignUp extends Component {
    state = {
        name: '',
        mail: '',
        code: '',
        phone: '',
        url: '',
        agreement: <a href="/agreement" target="_blank">Service</a>
    }
    postSignUp = () => {
        const enterprise = {};
        this.state.Input.map(x => {
            enterprise[x.name] = document.getElementsByName(x.name)[0].value;
        })
        console.log(enterprise)
        axios.post(`http://localhost:64430/api/Enterprises`, enterprise).then(x => {
            this.props.history.replace('/enterprises/login');
            console.log(x);
        }
        ).catch(x => {
            document.getElementById("message").innerHTML = x.response.data.Message;
        });
    }
    handleChangeName = (e) => {
        if (e.target.value.match("^[a-zA-Z ]*$") != null)
            this.setState({ name: e.target.value });
    }
    handleChangePhone = (e) => {
        if (e.target.value.match("^[0-9]*$") != null)
            this.setState({ phone: e.target.value });   
    }
    render() {

        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form onSubmit={this.postSignUp} size='large'>
                        <Segment stacked>
                            <Form.Input
                                name='Name' icon='user' iconPosition="left" placeholder='Name' required={true} type='text' value={this.state.name} maxLength={40} onChange={this.handleChangeName}>
                            </Form.Input>
                            <Form.Input
                                name='Email' icon='envelope' iconPosition="left" placeholder='Email' required={true} type='email' maxLength={50} onChange={(event) => this.setState({ email: event.target.value })} value={this.state.mail}>
                            </Form.Input>
                            <Form.Input
                                name='Code' icon="lock" iconPosition="left" placeholder='Password' required={true} type='password' maxLength={15} onChange={(event) => this.setState({ code: event.target.value })} value={this.state.code}>
                            </Form.Input>
                            <Form.Input
                                name='Phone' icon="phone" iconPosition="left" placeholder='Phone' required={true} type='text' maxLength={10} onChange={this.handleChangePhone} value={this.state.phone}>
                            </Form.Input>
                            <Form.Input
                                name='Url' icon="paper plane" iconPosition="left" placeholder='Url' required={true} type='text' maxLength={100} onChange={(event) => this.setState({ url: event.target.value })} value={this.state.url}>
                            </Form.Input>
                            <input type="Checkbox" required></input>
                            <label>I agree to the Terms of {this.state.agreement}</label>
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Sign Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
        </div >
        )
    }
}
export default EnterpriseSignUp;
