import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, Dimmer, Loader, Label, Checkbox } from 'semantic-ui-react';
import axios from 'axios';
import onChangeInput from "../../Service/onChangeInput";
import onSignUp from "../../Service/onSignUp";
class EnterpriseSignUp extends Component {
    state = {
        form: {
            Name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validation: {
                    required: true,
                    pattern: /^[a-zA-Z&.@ ]*$/,
                },
                valid: false,
            },
            Email: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z.+*%#!@]+$/,
                    mail: true
                },
                valid: false,
            },
            Password: {
                type: "Password",
                icon: "lock",
                text: "Password",
                value: "",
                maxLength: 15,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z]+$/,
                    Password: true
                },
                valid: false,
            },
            Phone: {
                type: "text",
                icon: "phone",
                text: "Phone",
                value: "",
                maxLength: 10,
                validation: {
                    required: true,
                    pattern: /^[0-9]*$/,
                    phone: true
                },
                valid: false,
            },
            Url: {
                type: "text",
                icon: "paper",
                text: "Url",
                value: "",
                maxLength: 100,
                validation: {
                    required: true,
                    pattern: /^/
                },
                valid: false,
            },
            Checkbox: {
                type: "checkbox",
                icon: "paper",
                text: "Checkbox",
                value: "",
                maxLength: 100,
                validation: {
                    required: true,
                    pattern: /^/
                },
                valid: false,
            },
        },
        trySend: false,
        loading: false,
        errorMessage: "",
    }
    onChangeHendler = (e, id) => {
        this.setState({ trySend: false })
        const form = { ...this.state.form };
        const inputChanged = { ...form[id] };
        onChangeInput(e, inputChanged, form, id);
        if (id === "Checkbox") {
                inputChanged.value =  !inputChanged.value;
        }
        this.setState({ form: form })
    }
    SignUp = () => {
        const form = { ...this.state.form };
        const obj = onSignUp(form)
        if (obj != null) {
            this.setState({ form: form, trySend: true, errorMessage: obj.error });
        }
        else {
            this.setState({ loading: true });
            const data = {};
            for (let x in this.state.form) {
                data[x] = this.state.form[x].value;
            }
            axios.post(`http://localhost:64430/api/Enterprises`, data).then(x => {
                this.props.history.replace('/enterprises/login');
                console.log(x);
            }).catch(x => {
                this.setState({ loading: false, errorMessage: x.response.data.Message });
            });
        }
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        console.log(array)
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Segment stacked>
                            {array.map((x, i) => {
                                console.log(x.config)
                                return <Form.Input
                                    key={i} iconPosition="left"
                                    maxLength={x.config.maxLength}
                                    type={x.config.type}
                                    onChange={event => this.onChangeHendler(event, x.id)}
                                    className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                                    placeholder={x.config.text + "*"}
                                    icon={x.config.icon}
                                    label={x.id == "Checkbox" ? <label style={{ position: "absolute" }}>I accept the <a href="/agreement" target="_blank">Terms of Service</a></label> : ""}
                                >
                                </Form.Input>
                            })}
                            <div id="message" style={{ color: "red" }}>{this.state.errorMessage}</div>
                            {/* <Checkbox icon="paper" onChange={e=>this.setState} label={<label>I accept the <a href="/agreement" target="_blank">Terms of Service</a></label>} /> */}
                            <Button type="submit" color='teal' fluid size='large'>
                                Sign-Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
            <Dimmer active={this.state.loading} inverted>
                <Loader size='large'>Loading</Loader>
            </Dimmer>
        </div >
        )
    }
}
export default EnterpriseSignUp;
