import React, { Component } from 'react'
import { Segment, List, Divider, Icon } from "semantic-ui-react"
class lotery extends Component {
    state = {
        num1: null,
        num2: null,
        Addition: null,
        different: null,
    }
    componentDidMount() {
        {
            this.props.Type === 1 ?
                this.setState({ different: "* Is valid for "+ this.props.Expiration +" month" })
                : this.props.Type === 2 ?
                    this.setState({ different: null })
                    :
                    this.setState({ different: "*  Is valid until "+this.props.ExpireDate })
        }
        {

            this.props.LoteryType === "Discount" ?
                this.setState({ num1: this.props.Sum, num2: this.props.SumType + "  Discount" })
                : this.props.LoteryType === "Plus Free" ?
                    this.setState({ num1: this.props.Num1 + " + ", num2: this.props.Num2 + "  Sale" })
                    :
                    this.setState({ num1: this.props.Num1 + " in ", num2: this.props.Num2 + "  $" })
        }
        this.setState({ Addition: this.props.Addition })
    }
    render() {
        console.log(this.props)
        return   <List.Item className="lottery" style={{fontSize:"20px",color:"white",backgroundColor: "#191a19",border:"groove 1px",marginBottom:"1vh",padding:"1vh",borderRadius:"20px",boxShadow: " 5px 10px 18px #888888" }}>  
                    <List.Icon  >
                        <Icon circular inverted color="teal" size="big" name={this.props.Type === 1 ? "user plus" : this.props.Type === 2 ? "birthday" : "credit card outline"}></Icon>
                    </List.Icon>
                    <List.Content>
                    {/* {this.props.Type ===2 ?<div style={{textAlign:"center"}}>Congradulations</div>:""} */}
                        <List.Header style={{fontSize:"20px",color:"rgb(0 181 173)",fontWeight: "bolder"}}>{this.props.Type === 1 ? "New Member" : this.props.Type === 2 ? "Birthday" : "Club Members"}</List.Header>
                       <div style={{textAlign:"center",display:"inline-block"}}> You have {this.state.num1}{this.state.num2}</div>
                       <div style={{textAlign:"right",fontSize:"15px"}}> {this.state.Addition+" "+this.state.different}</div>
                    </List.Content>
                </List.Item> 
    }
}
export default lotery;
