import React, { Component } from 'react'
import axios from 'axios';
import Store from './MyStore';
import seeMore from "../Service/SeeMore"
class MyStores extends Component {
    state = {
        stores: [],
        shown: [],
        stopped: 0,
        length: "",

    }
    componentDidMount() {
        axios.get(`http://localhost:64430/api/Enterprises`).then(x => {
            this.setState({ stores: x.data, length: x.data.length });
            var showIt = seeMore(this.state.length, this.state.stopped, this.state.stores);
            this.setState({ shown: showIt });
            var nowStopped = (this.state.stopped + this.state.shown.length);
            this.setState({ stopped: nowStopped });
            var nowLength = (this.state.length - this.state.stopped);
            this.setState({ length: nowLength });
            console.log(this.state.shown, nowStopped, nowLength)

        });
    }
    handelClick = () => {
        if(this.state.shown!==null){
        var showIt = seeMore(this.state.length, this.state.stopped, this.state.stores);
        this.setState({ shown: showIt });
        var nowStopped = (this.state.stopped + this.state.shown.length);
        this.setState({ stopped: nowStopped });
        var nowLength = (this.state.length - this.state.stopped);
        this.setState({ length: nowLength });
        console.log(this.state.shown, nowStopped, nowLength)
    }
    }
    render() {
        return (
            <div>
                <h3 style={{ backgroundColor: "#00b5ad", color: "white", textAlign: "left", paddingLeft: "1%" }}>Customers</h3>
                <button onClick={this.handelClick}>see more</button>
                <div>
                    {this.state.shown !== null ? this.state.shown.map((x, i) => {
                        return <Store name={x.Name} url={x.Url} key={i}></Store>
                    }) : <div>No results</div>}
                </div>
            </div>
        )
    }
}
export default MyStores;
