import React, { Component } from 'react'
import { Segment, List, Divider } from "semantic-ui-react"
class lotery extends Component {
    state = {
        num1: null,
        num2: null,
        Addition: null,
        Duration:null,
        different: null
    }
    componentDidMount() {
        {
            this.props.Type === 1 ?
                this.setState({ different: this.props.Expiration })
                : this.props.Type === 2 ?
                    this.setState({ different: null })
                    :
                    this.setState({ different: this.props.ExpireDate })
        }
        {

            this.props.LoteryType === "Discount" ?
                this.setState({ num1: this.props.Sum, num2: this.props.SumType + "  Discount" })
                : this.props.LoteryType === "Plus Free" ?
                    this.setState({ num1: this.props.Num1 + " + ", num2: this.props.Num2 + "  Sale" })
                    :
                    this.setState({ num1: this.props.Num1 + " in ", num2: this.props.Num2 + "  $" })
        }
        this.setState({ Addition: this.props.Addition })
    }
    render() {
        console.log(this.props)
        return   <List.Item className="lottery" style={{fontSize:"25px",color:"white",backgroundColor: "rgb(40, 44, 52)",border:"groove 1px",marginBottom:"2vh",padding:"2vh",borderRadius:"20px"}}>  
                    <List.Icon size="big" name={this.props.Type === 1 ? "user plus" : this.props.Type === 2 ? "birthday" : "credit card outline"}></List.Icon>
                    <List.Content>
                    {/* {this.props.Type ===2 ?<div style={{textAlign:"center"}}>Congradulations</div>:""} */}
                        <List.Header style={{fontSize:"20px",color:"green"}}>{this.props.Type === 1 ? "New Member" : this.props.Type === 2 ? "Birthday" : "Club Members"}</List.Header>
                       <div style={{textAlign:"center",display:"inline-block"}}> You have {this.state.num1}{this.state.num2}</div>
                        <div style={{textAlign:"right",fontSize:"15px"}}>{this.state.Addition !== null ? "*" + this.state.Addition : ""} {this.state.Duration !== null ? "*" + this.state.Duration : ""}</div>
                    </List.Content>

                </List.Item>
    }
}
export default lotery;
