import * as actionName from './actionTypes'
const initalState={
    user:{},
    errorLogin:"",
    loading:false
}

const rootReducer=(state=initalState,action)=>{
    switch(action.type){
        case actionName.selectUser:
            return{
                ...state,
                user:action.payload
            }
     
     default:return state
    }
    
}
export default rootReducer;
