import React, { Component } from 'react'
import Lotery from './Lotery';
import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, Select, Input, Label } from 'semantic-ui-react';
import { NavLink, withRouter } from 'react-router-dom';

class Lotteries extends Component {
    state = {
        new: false,
        birthday: false,
        club_members: false,
    }
    render() {
        return (
            <div>
                <Grid.Column style={{ maxWidth: 450, boxShadow: '5px 2px 1px -1px rgba(0, 0, 0, 0.2), 5px 5px 5px 5px rgba(0, 0, 0, 0.14), 5px 1px 7px 4px rgba(0, 0, 0, 0.12)', borderRadius: '10%', height: '60%' }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        Lottery For Newers
                        <Button onClick={event => this.setState({ new: !this.state.new })}>{this.state.new ? "delete" : "insert"}</Button>
                        {this.state.new == true ? <Lotery></Lotery> : ""}
                    </Header>
                </Grid.Column>
                <Segment>
                </Segment>
                {/* <Checkbox label="lotery For New" onChange={event => this.setState({ new: !this.state.new })}></Checkbox> */}
{/* 
                <Checkbox label="lotery for Birthday" onChange={event => this.setState({ birthday: !this.state.birthday })}></Checkbox>
                {this.state.birthday == true ? <Lotery></Lotery> : ""}
                <Checkbox label="lotery for club members" onChange={event => this.setState({ club_members: !this.state.club_members })}></Checkbox>
                {this.state.club_members == true ? <Lotery></Lotery> : ""} */}
            </div>
        )
    }
}

export default Lotteries
