import validForm from '../../Service/ValidStateForm';
const onSignUp = (form) => {
    var obj = validForm(form);
    for (let x in form) {
        const inputError = { ...form[x] };
        inputError.valid = true;
        form[x] = inputError;
    }
    if (obj !== null) {
        const inputError = { ...form[obj.id] };
        inputError.valid = false;
        form[obj.id] = inputError;
        return true;
    }
    return false;
}
export default onSignUp;
