import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Input, Select, Form, Dropdown, Grid, Segment, Radio, Header, Image, TextArea, Popup, Button, Icon, Label } from 'semantic-ui-react';
class Lotery extends Component {
    state = {
        statusLotery: -1,
        loteryName: "",
        loteryTypes: [
            { key: 'Discount', text: 'Discount', value: 'Discount' },
            { key: "Plus Free", text: "Plus Free", value: "Plus Free" },
            { key: "X in Y", text: "X in Y", value: "X in Y" }],
        sumTypes: [
            { key: '%', text: '%', value: '%' },
            { key: '$', text: '$', value: '$' }
        ],
        form: {
            loteryType: "Discount",
            sum: "",
            sumType: "",
            num1: "",
            num2: "",
            addition: "",
            expireDate: "",
        },

    }
    hanleSelctOnChange = (event, value) => {
        const form = { ...this.state.form }
        for (let x in this.form) {
            x=""
        }
        form.loteryType = value;
        this.setState({ form: form });


    }
    render() {
        return (
            <div style={{ textAlign: "left" }}>
                <Form style={{ marginLeft: "9%" }}>
                    <Grid >
                        <Grid.Column style={{ width: 950 }}>
                            <Grid.Row>
                                <Form.Group widths="equal">
                                    <Form.Select label="Lottery Types" options={this.state.loteryTypes}
                                        defaultValue="Discount"
                                        onChange={(event, { value }) => this.hanleSelctOnChange(event, value)}></Form.Select>
                                    {this.state.form.loteryType === "Discount" ?
                                        <Popup
                                            content="for example:30% or 30$"
                                            trigger={<Form.Input
                                                label="Details"
                                                type="number"
                                                labelPosition='right'
                                                action={
                                                    <Dropdown button basic floating options={this.state.sumTypes} defaultValue='$' />}></Form.Input>} /> : this.state.form.loteryType === "Plus Free" ?
                                            <Form.Input label="Details">
                                                <Popup content="for example:1+1" trigger={<Input type="number" min={1} max={10}></Input>} /> 
                                             +
                                               <Popup content="for example:1+1" trigger={<Input type="number" min={1} max={10}></Input>} /></Form.Input> :
                                            <Form.Input label="Details">
                                                <Popup content="for example:3 In 100$" trigger={<Input type="number" min={1} max={10}></Input>} /> 
                                                In
                                            <Popup content="for example:3 In 100$" trigger={<Input type="number" min={1} max={10000}></Input>} /></Form.Input>}
                                    <Form.Input type="date" label="Expire Date" value={this.state.form.expireDate} onChange={(e, { value }) => {
                                        const form = { ...this.state.form }
                                        form["expireDate"] = value
                                        this.setState({ form: form })
                                    }} >
                                    </Form.Input>
                                </Form.Group>
                            </Grid.Row>
                            <Grid.Row>
                                <Form.TextArea label="Addition" type="text" placeholder="Addition" maxLength={50}></Form.TextArea></Grid.Row></Grid.Column></Grid>
                </Form>

            </div >
        )
    }
}

export default Lotery;
