import React, { Component } from 'react';
import { Form, Grid, Header, Input, Popup, Divider } from 'semantic-ui-react';
import { InputFile } from 'semantic-ui-react-input-file';
import { connect } from 'react-redux'
import { selectCard } from "../../../Store/actions/EnterpriseAction"
import axios from 'axios';
class CardDetails extends Component {
    // componentDidUpdate() {
    //     if (this.props.submit && this.props.card === null) {
    //         const data = {};
    //         for (let x in this.props.form) {
    //             data[x] = this.props.form[x].value;
    //         }
    //         this.props.selectCard(data)
    //     }
    // }
    selected = "";
    componentDidMount() {
        const path = '/enetrprises/UpdateEnterpCard';
        if (this.props.location === path) {
            if (this.props.form.Type.value === this.props.form.Type.options[0].value)
                this.selected = this.props.form.Type.options[0].value;
            else
            this.selected = this.props.form.Type.options[1].value;
        }
    }
    render() {
        const path = '/enetrprises/UpdateEnterpCard';
        const form = this.props.form;
        const array = [];
        for (let x in form) {
            array.push({ id: x, config: form[x] })
        }
        console.log(form.Type.value, form.Type.options[0].value)
        return (<div style={{ marginLeft: "1vw" }}>
            <Header as='h2' color='teal' textAlign='left'>Card Details</Header>
            <Divider></Divider>
            <Form>
                <Grid><Grid.Column><Form.Group>
                    <Form.Input label={form.Cost.label}>
                        <Input label={form.Cost.icon} min={0} labelPosition="right" type={form.Cost.type} onChange={event => this.props.onChange(event.target.value, "Cost")} value={form.Cost.value}
                            // className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id} 
                            placeholder={form.Cost.text}
                            step={form.Cost.step}
                        /></Form.Input>
                    <Form.Input label={form.CountPoints.label}><Popup content="for example : 5 point =1$" trigger={<Input min={1} onChange={event => this.props.onChange(event.target.value, "CountPoints")} value={form.CountPoints.value} type={form.CountPoints.type} label={form.CountPoints.icon} labelPosition="right" placeholder={form.CountPoints.text} />}
                    />
                    </Form.Input>
                    <Form.Select label={form.Type.label} placeholder={form.Type.text}
                        defaultValue={form.Type.value===form.Type.options[0].value?form.Type.options[1].value:form.Type.options[0].value}
                        options={form.Type.options} onChange={this.props.onSelect} ></Form.Select>
                    <Form.Input label={form.Img.label}>
                        <InputFile
                            button={{
                                labelPosition: "left",
                                label: <Input placeholder={form.Img.text} value={this.props.fileName !== "" ? this.props.fileName : ""}></Input>
                            }}
                            input={{
                                onChange: this.props.convertImg,
                            }} /></Form.Input>
                    <Popup content="for example : the card is valid for 10 years" trigger={
                        <Form.Input label={form.Duration.label} min={0} onChange={event => this.props.onChange(event.target.value, "Duration")}
                            value={form.Duration.value} type="number"
                            placeholder={form.Duration.text}
                            step={form.Duration.step} >
                        </Form.Input>}></Popup>
                </Form.Group></Grid.Column></Grid></Form>
        </div>
        )
    }
}
// const mapStateToProps = state => {
//     return {
//         card: state.enterprise.card,
//         cards: state.enterprise.cards,
//     }
// }
// const mapDispatchToProps = dispatch => {
//     return {
//         // selectCard: (card) => dispatch(selectCard(card)),
//     }
// }
export default CardDetails;
