import React, { Component } from 'react'
import Lotery from './Lotery';
import "../../Public.css";
import 'semantic-ui-css/semantic.min.css';
import { connect } from 'react-redux'
import { Button, Form, Header, Divider, Dimmer, Loader, Message } from 'semantic-ui-react';
import CardDetails from "./CardDetails"
import { postNewCard } from "../../../Store/actions/EnterpriseAction"
import axios from 'axios';
class NewCard extends Component {
    state = {
        lotteries: {
            new: {
                header: "Lottery For Newers",
                active: false,
            },
            birthday: {
                header: "Lottery For Birthday",
                active: false,
            },
            club_members: {
                header: "Lottery For Club Members",
                active: false,
            }
        },
        lotery: [],
        card: {}
    }
    onClick = (event, x) => {
        event.preventDefault();
        const lotteries = { ...this.state.lotteries };
        const lotery = { ...lotteries[x.id] };
        lotery.active = !lotery.active;
        lotteries[x.id] = lotery;
        this.setState({ lotteries: lotteries })
    }
    changeLotery = (index, value) => {
        const lotery = [...this.state.lotery];
        lotery[index] = value;
        this.setState({ lotery: lotery })
    }
    changeCard = (card) => {
        this.setState({ card: card })
    }
    onSubmit = () => {
       this.props.loading();
       postNewCard(this.state.card,this.state.lotteries,this.props.enterprise.enterpId).then(x=>{
         this.props.history.replace('clearing'); 
       })
       .catch(x=>{this.props.errorFiled(x.response.data.Message)})
    }
    render() {
        const array = [];
        for (let x in this.state.lotteries) {
            array.push({ id: x, config: this.state.lotteries[x] })
        }
        return (
            <div>
                <h1 style={{ width: '30vw', marginLeft: '35vw', marginTop: '5vh', textAlign: 'center', fontSize: '180%', color: 'rgb(40, 44, 52)' }}>New Card</h1>
                <CardDetails changed={this.changeCard}></CardDetails>
                <Header as='h2' color='teal' textAlign='left' style={{ marginLeft: "1vw" }}> Lotteries </Header>
                <Form onSubmit={this.onSubmit}>
                    {array.map((x, i) => {
                        return <div>
                            <Divider></Divider>
                            <Header as='h4' color='teal' textAlign='left'>
                                <Button style={{ marginRight: "3vw", marginLeft: "1vw" }} onClick={event => this.onClick(event, x)}>{x.config.active ? "Delete" : "Insert"}</Button>
                                {x.config.header}
                            </Header>
                            {x.config.active == true ? <Lotery index={i} changed={this.changeLotery}></Lotery> : ""}
                            {/* {x.id==="club_members"?<Button>Insert</Button>:""} */}
                        </div>
                    })}
                    <Button type="submit" color='teal' style={{ fontSize: "18px", marginTop: "9vh", padding: "2vh", width: "25%", backgroundColor: "#282c34", marginLeft: "38%" }}>Submit And Pay</Button>
                </Form>
                <Dimmer id='dimmer' active={this.props.enterp_loading} inverted='false'>
                    <Loader size='large'>Loading</Loader>
                </Dimmer>
                <div style={{ color: "red" ,textAlign:"center"}}>{this.props.error}</div>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        enterprise: state.enterprise.enterprise,
        enterp_loading: state.enterprise.loading,
        error: state.enterprise.error,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        loadingNewCard:()=> dispatch({ type: "LOADING", payload: true }),
       
      errorFiled:(errorMsg)=>  dispatch({ type: "ERROR", payload:errorMsg  }),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(NewCard);
