import React, { Component } from 'react'
import axios from 'axios';
import { connect } from 'react-redux'
import Card from './Card';
import Lotery from './Lotery';
import { Button,Divider, Grid,Segment,List } from 'semantic-ui-react';

class Cards extends Component {
    state = {
        cards: [],
        loading: true,
        value: "loading..."
    }
    // ${this.props.user.C_id}
    componentDidMount() {
        this.setState({ loading: true });
        axios.get(`http://localhost:64430/api/ClubCards?userId=2`).then(res => {
            this.setState({ cards: res.data })
            console.log(this.state.cards);
            this.setState({ loading: false });
        }).catch(x => {
            this.setState({ value: x.response.data.Message })
        })
    }
    render() {
        return <div>
            <h1 style={{ color: '#00b5ad', textAlign: "center", fontSize: '20px', border: 'none' }}>| My Club Cards |</h1>
            {this.state.cards !== null ?
                this.state.cards.map((x, i) => {
                    return <Grid style={{height:"40vh"}}>
                        <Divider></Divider>
                        <Grid.Column width={4}>
                        <Card name={x.Card['Name']}
                            key={i}
                            expire={x.Card['ExpireDate']}
                            Img={x.Card['Img']}
                            type={x.Card['Type']}
                            points={x.Card['Points']}></Card></Grid.Column>
                       <Grid.Column width={8} style={{textAlign:"center"}}><Segment inverted><List divided inverted relaxed> {x.Lottery.map((y, j) => {
                            return   <Lotery key={j}
                                    Type={y.Type}
                                    Num1={y.Num1}
                                    Num2={y.Num2}
                                    SumType={y.SumType}
                                    Sum={y.Sum}
                                    LoteryType={y.LoteryType}
                                    Addition={y.Addition}
                                    Expiration={y.Expiration}
                                    ExpireDate={y.ExpireDate}
                                >
                                </Lotery>
                              
                        })}        </List> </Segment></Grid.Column>
                           <Grid.Column width={2}> <a href={x.Card['Url']}>
                            <Button style={{ color: 'white', backgroundColor: 'rgb(40, 44, 52)', width: "8vw", height: "6vh", borderRadius: "10px" }}>
                                For realization<br></br>in web side</Button>
                        </a></Grid.Column>
                    </Grid>
                })
                : "No Results"
            }

        </div>

    }
}
const mapStateToProps = state => {
    return {
        user: state.user.user
    }
}
export default connect(mapStateToProps, null)(Cards);
