import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, Dimmer, Loader, Label, Checkbox } from 'semantic-ui-react';
import axios from 'axios';
import onChangeInput from "../../Service/onChangeInput";
import onSignUp from "../../Service/onSubmit";
import validCheckbox from "../../Service/validCheckbox";
import { connect } from 'react-redux'
class EnterpriseSignUp extends Component {
    state = {
        form: {
            Name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validation: {
                    required: true,
                    pattern: /^[a-zA-Z&.@ ]*$/,
                },
                valid: false,
            },
            Email: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z.+*%#!@]+$/,
                    mail: true
                },
                valid: false,
            },
            Password: {
                type: "Password",
                icon: "lock",
                text: "Password",
                value: "",
                maxLength: 15,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z]+$/,
                    password: true
                },
                valid: false,
            },
            Phone: {
                type: "text",
                icon: "phone",
                text: "Phone",
                value: "",
                maxLength: 10,
                validation: {
                    required: true,
                    pattern: /^[0-9]*$/,
                    phone: true
                },
                valid: false,
            },
            Url: {
                type: "text",
                icon: "share",
                text: "Url",
                value: "",
                maxLength: 100,
                validation: {
                    required: true,
                    pattern: /^/,
                    url: true
                },
                valid: false,
            },
        },
        checkbox: false,
        trySend: false,
        loading: false,
        errorMessage: "",
        update: false
    }
    componentDidMount() {
        if (this.props.location.pathname === '/enterprises/ProfileUpdate') {
            const form = { ...this.state.form };
            this.setState({ checkbox: true })
            form['Email'].value = this.props.enterprise['Email']
            form['Name'].value = this.props.enterprise['Name']
            form['Password'].value = this.props.enterprise['Password']
            form['Phone'].value = this.props.enterprise['Phone']
            form['Url'].value = this.props.enterprise['Url']
            for (let x in this.state.form) {
                this.state.form[x].valid = true;
            }
            this.setState({ form: form })
        }

    }
    onChangeHendler = (e, id) => {
        this.setState({ trySend: false })
        const form = { ...this.state.form };
        const inputChanged = { ...form[id] };
        onChangeInput(e, inputChanged, form, id);
        this.setState({ form: form })
    }

    SignUp = () => {
        const form = { ...this.state.form };
        var obj = onSignUp(form)
        if (obj === null)
            obj = validCheckbox(this.state.checkbox)
        if (obj !== null) {
            this.setState({ form: form, trySend: true, errorMessage: obj.error });
        }
        else {
            this.setState({ loading: true });
            const data = {};
            for (let x in this.state.form) {
                data[x] = this.state.form[x].value;
            }
            const path=`http://localhost:64430/api/Enterprises`;
            const action=  this.props.history.replace('/enterprises/login');;
            if (this.props.location.pathname === '/enterprises/ProfileUpdate') {
                axios.put(path, data).then(x => {
                  action;
                }).catch(x => {
                    this.setState({ loading: false, errorMessage: x.response.data.Message });
                });
            }
            else {
                axios.post(path, data).then(x => {
                    this.props.history.replace('/enterprises/login');
                }).catch(x => {
                    this.setState({ loading: false, errorMessage: x.response.data.Message });
                });
            }
        }
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450, boxShadow: '5px 2px 1px -1px rgba(0, 0, 0, 0.2), 5px 5px 5px 5px rgba(0, 0, 0, 0.14), 5px 1px 7px 4px rgba(0, 0, 0, 0.12)', borderRadius: '10%', height: '67%', width: '37vw' }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        {this.props.location.pathname === '/enterprises/ProfileUpdate' ? " Update Profile" : "Create an account"}
                        <br></br>
                        <Image src='/Images/profile.PNG' style={{ height: '15vh', width: '8vw' }} />
                    </Header>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Segment stacked>
                            {array.map((x, i) => {
                                console.log(x)
                                return <Form.Input
                                    key={i} iconPosition="left"
                                    maxLength={x.config.maxLength}
                                    type={x.config.type}
                                    onChange={event => this.onChangeHendler(event, x.id)}
                                    className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                                    placeholder={x.config.text + "*"}
                                    value={this.state.form[x.id].value}
                                    icon={x.config.icon}>
                                </Form.Input>
                            })}
                            <div id="message" style={{ color: "red" }}>{this.state.errorMessage}</div>
                            <Checkbox
                                style={{ display: this.props.location.pathname === '/enterprises/ProfileUpdate' ? 'none' : 'block' }}
                                checked={this.state.checkbox} onChange={e => this.setState({ checkbox: !this.state.checkbox })} label={<label>I accept the <a href="/agreement" target="_blank">Terms of Service</a></label>} />
                            <Button type="submit" color='teal' fluid size='large'>
                                {this.props.location.pathname === '/enterprises/ProfileUpdate' ? "Update" : "Create Account"}
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
            <Dimmer active={this.state.loading} inverted>
                <Loader size='large'>Loading</Loader>
            </Dimmer>
        </div >
        )
    }
}
const mapStateToProps = state => {
    return {
        enterprise: state.enterprise.enterprise
    }
}
export default connect(mapStateToProps, null)(EnterpriseSignUp);
