import validator from 'validator';
const newPassword = (mail) => {
    if (mail.value === "") {
        return { id: "Email", error: "mail is requried" };
    }
    if (!validator.isEmail(mail.value))
        return { id: "Email", error: "mail is not valid" };
    return null;
}
export default newPassword;
