import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Input, Select, Form, Dropdown, Grid, Segment, Radio, Header, Image } from 'semantic-ui-react';
class Lotery extends Component {
    state = {
        statusLotery: -1,
        loteryName: "",
        loteryTypes: [
            { key: 'Discount', text: 'Discount', value: 'Discount' },
            { key: "Plus Free", text: "Plus Free", value: "Plus Free" },
            { key: "X in Y", text: "X in Y", value: "X in Y" }],
        sumTypes: [
            { key: '%', text: '%', value: '%' },
            { key: '$', text: '$', value: '$' }
        ],
        form: {
            loteryType: "Discount",
            sum: "",
            sumType: "",
            num1: "",
            num2: "",
            addition: "",
            expireDate: "",
        },

    }
    hanleSelctOnChange = (event, value) => {
        const form = { ...this.state.form }
        form.loteryType = value;
        form.expireDate = "";
        // this.setState({ loteryType: value });
        this.setState({ form: form });


    }
    //     <Grid columns='equal'>
    //     <Grid.Row>
    //       <Grid.Column>
    //         <Segment>1</Segment>
    //       </Grid.Column>
    //       <Grid.Column>
    //         <Segment>2</Segment>
    //       </Grid.Column>
    //       <Grid.Column>
    //         <Segment>3</Segment>
    //       </Grid.Column>
    //       <Grid.Column>
    //         <Segment>4</Segment>
    //       </Grid.Column>
    //     </Grid.Row>
    //     <Grid.Row>
    //       <Grid.Column>
    //         <Segment>1</Segment>
    //       </Grid.Column>
    //       <Grid.Column>
    //         <Segment>2</Segment>
    //       </Grid.Column>
    //       <Grid.Column>
    //         <Segment>3</Segment>
    //       </Grid.Column>
    //     </Grid.Row>
    //     <Grid.Row>
    //       <Grid.Column>
    //         <Segment>1</Segment>
    //       </Grid.Column>
    //       <Grid.Column>
    //         <Segment>2</Segment>
    //       </Grid.Column>
    //     </Grid.Row>
    //   </Grid>
    // )
    render() {
        return (
            <div>
                <Grid>
                    <Grid.Row columns={3}></Grid.Row>
                        <Grid.Column>
                            <Select
                                label='Lotery Type'
                                options={this.state.loteryTypes}
                                defaultValue="Discount"
                                onChange={(event, { value }) => this.hanleSelctOnChange(event, value)}
                            />
                        </Grid.Column>
                        <Grid.Column>
                            <Input type="text" value={this.state.form.expireDate} placeholder="expireDate"
                                onChange={(e, { value }) => {
                                    const form = { ...this.state.form }
                                    form["expireDate"] = value
                                    this.setState({ form: form })
                                }}
                                value={this.state.form.expireDate}
                                onFocus={(e) => e.target.type = 'date'}
                            >
                            </Input>
                        </Grid.Column>
                        <Grid.Column>  <Input type="text" placeholder="Addition" maxLength={50}></Input> </Grid.Column>
                         {this.state.form.loteryType === "Discount" ?
                             <Grid.Column><Input type="number"
                                action={<Dropdown options={this.state.sumTypes} defaultValue={"$"} />} /> </Grid.Column>:
                            this.state.loteryType === "Plus Free" ?  <Grid.Column><div><Input></Input>+<Input></Input></div></Grid.Column> :
                            <Grid.Column> <div><Input></Input>In<Input></Input></div> </Grid.Column>}
                </Grid>
            </div>
        )
    }
}

export default Lotery;
