const seeMore = (length, stoppped, stores) => {
    var showIt = [];
    var i;
    if (length >= 3) {
        for (i = 0; i < 3; i++) {
            showIt[i] = stores[i + stoppped];
        }
    }
    else {
        if(length>0){
        for (i = stoppped; i < length; i++) {
            showIt[i - stoppped] = stores[i + stoppped];
        }}
        else{
            showIt=null;   
        }
    }
    return showIt;
}
export default seeMore;
