import React, { Component } from 'react'
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid,Icon, Header, Image, Segment, FormInput, Input, TextArea, Label, Divider } from 'semantic-ui-react';
class ContactUs extends Component {
    state = {
        form: {
            name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validition: {
                    required: true,
                    pattern: /^[a-zA-Z ]*$/
                },
                error: "The name is incorrect",
                valid: false,
                toched: false
            },
            mail: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validition: {
                    required: true,
                    pattern: /^/
                    // pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
                },
                error: "The email is incorrect",
                valid: false,
                toched: false
            },
            //     <Form.Input
            //     className='Phone' icon="phone" iconPosition="left" placeholder='Phone' type="text" maxLength={10} value={this.state.phone} onChange={this.handleChangePhone}>
            // </Form.Input>
            phone: {
                type: "text",
                icon: "phone",
                text: "Phone",
                value: "",
                maxLength: 10,
                validition: {
                    required: true,
                    pattern: /^[0-9]*$/
                },
                error: "The phone is incorrect",
                valid: false,
                toched: false
            },
        },
        validForm: false,
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div>
            <h1 style={{textAlign:"left",borderBottom:"solid black",fontSize:"16px",paddingLeft:"1%"}}>Contact Us</h1>

            <Grid textAlign='center' celled='internally' style={{ height: '60vh' }} verticalAlign='middle'>
                {/* <Grid.Row> */}
                    <Grid.Column width={11} style={{}}>
                        <Form size='large' onSubmit={this.SignUp}>
                            <Form.Group >
                                <Form.Input placeholder='Name' width={14} />
                            </Form.Group>
                            <Form.Group>
                            <Form.Input placeholder='Email' width={7} />
                                <Form.Input placeholder='Phone' width={7} />
                            </Form.Group>
                            <Form.Group>
                            <Form.TextArea placeholder="Description" width={14}></Form.TextArea>
                            </Form.Group>
                            <div id="message" style={{ color: "red" }}></div>
                            <Form.Group>
                            <Form.Button width={14} type="submit" color='teal' >
                                Contact Me
                                </Form.Button>
                                </Form.Group>
                            {/* </Segment> */}
                        </Form>
                    </Grid.Column>

                    <Grid.Column width={4} style={{ minWidth: 170,paddingBottom:"8%",paddingLeft:"8%",borderRight:"dashed 1px",borderTop:"dashed 1px",textAlign:"left" }}  >
                        <Label style={{ display: "block",padding:"3%",backgroundColor:"transparent" }}><Icon name="phone"></Icon>Email:easyclub100@gmail.com</Label>
                        <Label style={{ display: "block" ,padding:"3%",backgroundColor:"transparent"}}>Fax:03-9258478</Label>
                        <Label style={{ display: "block" ,padding:"3%",backgroundColor:"transparent"}}>Phone:0527689412</Label>
                    </Grid.Column>
                {/* </Grid.Row> */}
            </Grid>

        </div>

        )
    }
}
export default ContactUs
