import React, { Component } from 'react'
import Lotery from './Lotery';
import "../../Public.css";
import 'semantic-ui-css/semantic.min.css';
import { connect } from 'react-redux'
import { Button, Form, Header, Divider, Dimmer, Loader, Message } from 'semantic-ui-react';
import CardDetails from "./CardDetails"
import { postNewCard } from "../../../Store/actions/EnterpriseAction"
import axios from 'axios';
import validForm from "../../Service/ValidStateForm"
class NewCard extends Component {
    state = {
        lotery: {
            new: {
                header: "Lottery For Newers",
                active: false,
                LoteryType: "Discount",
                Sum: "",
                SumType: "$",
                Num1: "",
                Num2: "",
                Addition: "",
                ExpireDate: "",
            },
            birthday: {
                header: "Lottery For Birthday",
                active: false,
                LoteryType: "Discount",
                Sum: "",
                SumType: "$",
                Num1: "",
                Num2: "",
                Addition: "",
                ExpireDate: "",
            },
            club_members: {
                header: "Lottery For Club Members",
                active: false,
                LoteryType: "Discount",
                Sum: "",
                SumType: "$",
                Num1: "",
                Num2: "",
                Addition: "",
                ExpireDate: "",
            },
        },
        card: {},
        cardError: false,
        cardForm: {
            Cost: {
                label: "Price For Customer",
                icon: "$",
                type: 'number',
                step: 5,
                text: "Card Price *",
                value: "",
                validation: {
                    required: true,
                }
            },
            CountPoints: {
                label: "Count CountPoints",
                icon: " = 1 $",
                type: 'number',
                text: "Count CountPoints *",
                value: "",
                validation: {
                    required: true,
                }
            },
            Type: {
                options: [{ key: "Basic", text: 'Basic card', value: 'Basic card' },
                { key: "Primium", text: 'Primium card', value: 'Primium card' }],
                label: "Card Type",
                text: "Type *",
                value: "",
                validation: {
                    required: true,
                }
            },
            Img: {
                label: 'Enterprise Image',
                value: "",
                text: "select Img *",
                validation: {
                    required: false,
                    img: true,
                }
            },
            Duration: {
                label: 'Duration',
                value: "",
                step: 1,
                type: "number",
                text: "Card Duration *",
                validation: {
                    required: false,
                }
            }
        },
        fileName: "",
    }
    //card
    onSelectCard = (event, { value }) => {
        const form = { ...this.state.cardForm }
        const input = { ...form["Type"] }
        input.value = value;
        form["Type"] = input
        this.setState({ cardForm: form })
        const card = {}
        for (let x in this.state.cardForm) {
            card[x] = this.state.cardForm[x].value;
        }
        this.setState({ card: card })
    }
    convertImg = (event) => {
        const file = event.target.files[0];
        if (file) {
            var name = file.name;
            const reader = new FileReader();
            reader.readAsDataURL(file);
            console.log(reader)
            reader.onload = () => {
                this.onChangeCard(String(reader.result), "Img")
                this.setState({ fileName: name })
            }
        }
    }
    onChangeCard = (value, id) => {
        const form = { ...this.state.cardForm }
        const input = { ...form[id] }
        if (input.type === "number") {
            input.value = parseInt(value);
        }
        else {
            input.value = value;
        }
        form[id] = input
        this.setState({ cardForm: form })
        const card = {}
        for (let x in this.state.cardForm) {
            card[x] = this.state.cardForm[x].value;
        }
        this.setState({ card: card })
    }
    onClick = (event, type) => {
        event.preventDefault();
        const lotery = { ...this.state.lotery };
        const loterySelected = { ...lotery[type] };
        loterySelected.active = !loterySelected.active;
        lotery[type] = loterySelected;
        this.setState({ lotery: lotery })
    }
    //lottery
    onChangeLottery = (value, id, type) => {
        const form = { ...this.state.lotery };
        const loterySelected = { ...form[type] };
        loterySelected[id] = value;
        form[type] = loterySelected;
        this.setState({ lotery: form });
    }
    onSelectLottery = (event, { value }, type) => {
        const form = { ...this.state.lotery };
        const loterySelected = { ...form[type] };
        if (value.length > 1) {
            for (let x in loterySelected) {
                console.log(x)
                if (x !== "active" && x !== "header") {
                    console.log(x)
                    loterySelected[x] = "";
                }
            }
            loterySelected.LoteryType = value;
            loterySelected.SumType = "$"
            console.log(loterySelected)
        }
        else
            loterySelected.SumType = value;
        form[type] = loterySelected;
        this.setState({ lotery: form });
    }
    //main form
    onSubmit = () => {
        const data = { ...this.state.card };
        const lottery = []
        for (let x in this.state.lotery) {
            if (x.active === true)
                lottery[x] = {...this.state.lotery[x]};
        }
        console.log(lottery)
        data['Lotery'] = [...lottery];
        data['EnterpId'] = this.props.enterprise.C_id;
        this.props.loadingNewCard();
        console.log(data)
        postNewCard(data).then(x => {
            this.props.history.replace('clearing');
        }).catch(x => { this.props.errorFiled(x.response.data.Message) })
    }
    render() {
        const array = [];
        for (let x in this.state.lotery) {
            array.push({ id: x, config: this.state.lotery[x] })
        }
        console.log(this.props.indexedCard)
        return (
            <div>
                <h1 style={{ width: '30vw', marginLeft: '35vw', marginTop: '5vh', textAlign: 'center', fontSize: '180%', color: 'rgb(40, 44, 52)' }}>New Card</h1>
                <CardDetails error={this.state.cardError} fileName={this.state.fileName} convertImg={this.convertImg} onSelect={this.onSelectCard} onChange={this.onChangeCard} form={{ ...this.state.cardForm }}></CardDetails>
                <Header as='h2' color='teal' textAlign='left' style={{ marginLeft: "1vw" }}> Lotteries </Header>
                <Form onSubmit={this.onSubmit}>
                    {array.map((x) => {
                        return <div key={x.id}>
                            <Divider></Divider>
                            <Header as='h4' color='teal' textAlign='left'>
                                <Button style={{ marginRight: "3vw", marginLeft: "1vw" }} onClick={event => this.onClick(event, x.id)}>{x.config.active ? "Delete" : "Insert"}</Button>
                                {x.config.header}
                            </Header>
                            {x.config.active == true ? <Lotery index={x.id} onSelect={this.onSelectLottery} onChange={this.onChangeLottery} form={x.config}></Lotery> : ""}
                        </div>
                    })}
                    <Button type="submit" color='teal' style={{ fontSize: "18px", marginTop: "9vh", padding: "2vh", width: "25%", backgroundColor: "#282c34", marginLeft: "38%" }}>Submit And Pay</Button>
                </Form>
                <Dimmer id='dimmer' active={this.props.enterp_loading} inverted='false'>
                    <Loader size='large'>Loading</Loader>
                </Dimmer>
                <div style={{ color: "red", textAlign: "center" }}>{this.props.error}</div>
            </div>
        )
    }
}
const mapStateToProps = state => {
    return {
        enterprise: state.enterprise.enterprise,
        enterp_loading: state.enterprise.loading,
        error: state.enterprise.error,
        cards: state.enterprise.cards,
        indexedCard: state.enterprise.indexedCard,
    }
}
const mapDispatchToProps = dispatch => {
    return {
        loadingNewCard: () => dispatch({ type: "LOADING", payload: true }),
        errorFiled: (errorMsg) => dispatch({ type: "ERROR", payload: errorMsg }),
    }
}
export default connect(mapStateToProps, mapDispatchToProps)(NewCard);
