import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Input, Select, Form, Dropdown, Grid, Segment, Radio, Header, Image } from 'semantic-ui-react';
class Lotery extends Component {
    state = {
        statusLotery: -1,
        loteryName: "",
        loteryTypes: [
            { key: 'Discount', text: 'Discount', value: 'Discount' },
            { key: "Plus Free", text: "Plus Free", value: "Plus Free" },
            { key: "X in Y", text: "X in Y", value: "X in Y" }],
        sumTypes: [
            { key: '%', text: '%', value: '%' },
            { key: '$', text: '$', value: '$' }
        ],
        form: {
            loteryType: "Discount",
            sum: "",
            sumType: "",
            num1: "",
            num2: "",
            addition: "",
            expireDate: "",
        },

    }
    hanleSelctOnChange = (event, value) => {
        const form = { ...this.state.form }
        form.loteryType = value;
        form.expireDate = "";
        // this.setState({ loteryType: value });
        this.setState({ form: form });


    }
    render() {
        return (
            <div>
                <Form>
                    <Form.Group>
                        <Form.Select
                            label="Lottery Type"
                            options={this.state.loteryTypes}
                            defaultValue="Discount"
                            onChange={(event, { value }) => this.hanleSelctOnChange(event, value)}
                        />
                        <Form.Input label="Expire Date" type="number" value={this.state.form.expireDate} placeholder="expireDate"
                            onChange={(e, { value }) => {
                                const form = { ...this.state.form }
                                form["expireDate"] = value
                                this.setState({ form: form })
                            }}
                            value={this.state.form.expireDate}
                        // onFocus={(e) => e.target.type = 'date'}
                        >
                        </Form.Input>
                        {this.state.form.loteryType === "Discount" ?
                            <Form.Input label="Discount Sum" type="number"
                                action={<Form.Dropdown options={this.state.sumTypes} defaultValue={"$"} />} /> :
                            this.state.form.loteryType === "Plus Free" ?
                              <Form.Input type="number"></Form.Input>:
                                <div><Form.Input></Form.Input>In<Form.Input></Form.Input></div>}
                        <Form.Input  type="text" placeholder="Addition" maxLength={50}></Form.Input>
                    </Form.Group>
                </Form>
            </div >
        )
    }
}

export default Lotery;
