import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, FormInput } from 'semantic-ui-react';
import axios from 'axios';
class UsersSignUp extends Component {
   
    state = {
        form:{
        nameA:{
            type: "text",
            icon:"lock",
            text:"Confirm Password",
            value: "",
            validition: {
                required: true,
                maxLength:15
            },
            errer:"לא תקין השם משפחה",
            valid: false,
            toched: false
        },
        tzA:{
            type: "text",
            value: "",
            validition: {
                required: true,
                maxLength: 2
            },
            valid: false,
            toched: false
        },
    },
    validForm:false,
        name: '',
        tz: '',
        mail: '',
        password: '',
        confirm_password: '',
        phone: '',
        birthdate: ''
    
}
validValue(value,tergets){
    let valid = true;
    if (tergets.required) {

        valid = value !== "" && valid;
    }
    if (tergets.maxLength) {
        valid = value.length <= tergets['maxLength'] && valid;
    }
    if (tergets.minLength) {
        valid = value.length < tergets['maxLength'] && valid;
    }
    return valid;
}
onChangeHendler=(e,id)=>{
const form={...this.state.form};
const inputChange={...form[id]};
//if valid
const valid=this.validValue(e.target.value,inputChange.validition)
if(!(valid===false&&id==="phone")){
inputChange.value=e.target.value;
inputChange.valid=valid;
inputChange.toched=true;
const validForm=validForm(form);
form[id]=inputChange;
this.setState({form:form,validForm:validForm})

//if form valid?
}

}
    handleChangeMail = (e) => {
        this.setState({ mail: e.target.value });
        document.getElementsByClassName("Email")[0].classList.remove("error");
    }
    handleChangePassword = (e) => {
        this.setState({ password: e.target.value });
        document.getElementsByClassName("Password")[0].classList.remove("error");
    }
    handleChangeBirthDate = (e) => {
        this.setState({ birthdate: e.target.value });
        document.getElementsByClassName("BirthDate")[0].classList.remove("error");
    }
    handleChangeName = (e) => {
        if (e.target.value.match("^[a-zA-Z ]*$") != null)
            this.setState({ name: e.target.value });
        document.getElementsByClassName("Name")[0].classList.remove("error");
    }
    handleChangePhone = (e) => {
        if (e.target.value.match("^[0-9]*$") != null)
            this.setState({ phone: e.target.value });
        document.getElementsByClassName("Phone")[0].classList.remove("error");
    }
    handleChangeTz = (e) => {
        if (e.target.value.match("^[0-9]*$") != null)
            this.setState({ tz: e.target.value });
        document.getElementsByClassName("Tz")[0].classList.remove("error");
    }
    handleChangeConfirmPassword = (e) => {
        this.setState({ confirm_password: e.target.value });
        document.getElementsByClassName("Confirm_Password")[0].classList.remove("error");
    }
    SignUp = (value,regurales) => {

        if (this.state.name === "") {
            document.getElementsByClassName("Name")[0].classList.add("error");
            document.getElementById("message").innerHTML = "name is required"
        }
        else
            if (this.state.tz === "") {
                document.getElementsByClassName("Tz")[0].classList.add("error");
                document.getElementById("message").innerHTML = "identity is required"
            }
            else
                if (this.state.tz.length !== 9) {
                    document.getElementsByClassName("Tz")[0].classList.add("error");
                    document.getElementById("message").innerHTML = "identity must have 9 numbers"
                }
                else
                    if (this.state.mail === "") {
                        document.getElementsByClassName("Email")[0].classList.add("error");
                        document.getElementById("message").innerHTML = "mail is required"
                    }
                    else
                        if (!this.state.mail.match('@')) {
                            document.getElementsByClassName("Email")[0].classList.add("error");
                            document.getElementById("message").innerHTML = "mail must includes @"
                        }
                        else
                            if (this.state.password === "") {
                                document.getElementsByClassName("Password")[0].classList.add("error");
                                document.getElementById("message").innerHTML = "password is required"
                            }
                            else
                                if (this.state.confirm_password === "") {
                                    document.getElementsByClassName("Confirm_Password")[0].classList.add("error");
                                    document.getElementById("message").innerHTML = "confirm_password is required"
                                }
                                else
                                    if (this.state.confirm_password !==this.state.password) {
                                        document.getElementsByClassName("Confirm_Password")[0].classList.add("error");
                                        document.getElementById("message").innerHTML = "The passwords are not the same"
                                    }
                                    else
                                        if (this.state.phone === "") {
                                            document.getElementsByClassName("Phone")[0].classList.add("error");
                                            document.getElementById("message").innerHTML = "phone is required"
                                        }
                                        else
                                            if (this.state.birthdate === "") {
                                                document.getElementsByClassName("BirthDate")[0].classList.add("error");
                                                document.getElementById("message").innerHTML = "birthdate is required"
                                            }
                                            else {
                                                axios.post(`http://localhost:64430/api/Users`, {
                                                    Name: this.state.name,
                                                    Tz: this.state.tz,
                                                    Email: this.state.mail,
                                                    Password: this.state.password,
                                                    Phone: this.state.phone,
                                                    BirthDate: this.state.birthdate,
                                                })
                                                    .then(x => {
                                                        this.props.history.replace('/users/login');
                                                        console.log(x);
                                                    }
                                                    ).catch(x => {
                                                        document.getElementById("message").innerHTML = x.response.data.Message;
                                                    });
                                            }
    }
    _onFocus = (e) => {
        e.currentTarget.type = "date";
    }
    render() {
        const p=this.state.form.map(x=>{
            <Form.Input id="x."
            className="Name" icon='user' iconPosition="left" placeholder="Name" type="text" maxLength={40} value={this.state.name} onChange={this.handleChangeName}>
        </Form.Input>
        })
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Segment stacked>
                            
                            <Form.Input
                                className="Name" icon='user' iconPosition="left" placeholder="Name" type="text" maxLength={40} value={this.state.name} onChange={this.handleChangeName}>
                            </Form.Input>
                            <Form.Input
                                className="Tz" icon="tag" iconPosition="left" placeholder="Identity" type="text" maxLength={9} value={this.state.tz} onChange={this.handleChangeTz}>
                            </Form.Input>
                            <Form.Input
                                className='Email' icon='envelope' iconPosition="left" placeholder='E-mail address' type="text" maxLength={50} value={this.state.mail} onChange={this.handleChangeMail}>
                            </Form.Input>
                            <Form.Input
                                className='Password' icon="lock" iconPosition="left" placeholder='Password' type="password" maxLength={15} value={this.state.password} onChange={this.handleChangePassword}>
                            </Form.Input>
                            <Form.Input
                                className='Confirm_Password' icon="lock" iconPosition="left" placeholder='Confirm Password' type="password" maxLength={15} value={this.state.password} onChange={this.handleChangeConfirmPassword}>
                            </Form.Input>
                            <Form.Input
                                className='Phone' icon="phone" iconPosition="left" placeholder='Phone' type="text" maxLength={10} value={this.state.phone} onChange={this.handleChangePhone}>
                            </Form.Input>
                            <Form.Input onFocus={this._onFocus}
                                className='BirthDate' icon="birthday cake" iconPosition="left" placeholder='BirthDate' value={this.state.data} onChange={this.handleChangeBirthDate}>
                            </Form.Input>
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Sign-Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
        </div>
        )
    }
}
export default UsersSignUp;
