import React, { Component } from 'react'
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, FormInput, Input, TextArea, Label,Divider } from 'semantic-ui-react';
class ContactUs extends Component {
    state = {
        form: {
            name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validition: {
                    required: true,
                    pattern: /^[a-zA-Z ]*$/
                },
                error: "The name is incorrect",
                valid: false,
                toched: false
            },
            mail: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validition: {
                    required: true,
                    pattern: /^/
                    // pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
                },
                error: "The email is incorrect",
                valid: false,
                toched: false
            },
            //     <Form.Input
            //     className='Phone' icon="phone" iconPosition="left" placeholder='Phone' type="text" maxLength={10} value={this.state.phone} onChange={this.handleChangePhone}>
            // </Form.Input>
            phone: {
                type: "text",
                icon: "phone",
                text: "Phone",
                value: "",
                maxLength: 10,
                validition: {
                    required: true,
                    pattern: /^[0-9]*$/
                },
                error: "The phone is incorrect",
                valid: false,
                toched: false
            },
        },
        validForm: false,
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div>
  <Segment placeholder>
            {/* <Grid  style={{ height: '100%' }} verticalAlign='middle'>
                <Grid.Column style={{width:"70%" }}> */}
                    <Form  onSubmit={this.SignUp} style={{width:"30%",margin:"0%"}} >
                            {array.map((x, i) => {
                                return <Form.Input style={{width:"100%",margin:"0%" }} key={i} iconPosition="left"
                                    maxLength={x.config.maxLength} type={x.config.type}
                                    // onChange={event => this.onChangeHendler(event, x.id)}
                                    className={(!x.config.valid ? x.id : "error")}
                                    placeholder={x.config.text} icon={x.config.icon}>
                                </Form.Input>
                            })}
                            <Form.TextArea type="text" maxLength="100" placeholder="Reference details"></Form.TextArea>
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Contuct me
                                </Button>
                    </Form>
                {/* </Grid.Column> */}
                {/* <Grid.Column verticalAlign='middle' style={{width:"30%",maxWidth: 450 }} > */}
                    <Label style={{display:"block"}}>Email:easyclub100@gmail.com</Label>
                    <Label style={{display:"block"}}>Fax:03-9258478</Label>
                    <Label style={{display:"block"}}>Phone:0527689412</Label>
                {/* </Grid.Column> */}
            {/* </Grid> */}
          </Segment>
        </div>
        )
    }
}
export default ContactUs
