import React, { Component } from 'react'
import axios from 'axios';
import Store from './MyStore';

class MyStores extends Component {
    state = {
        stores: [],
        shown: [],
        stoppped: 0,
        length: "",

    }
    componentDidMount() {
        axios.get(`http://localhost:64430/api/Enterprises`).then(x => {
            this.setState({ stores: x.data, length: x.data.length });
            console.log(this.state.length);

            if (this.state.length >= 3) {
                var showIt = [];
                var i;
                for (i = 0; i < 4; i++) {
                    console.log(i);
                    showIt[i] = this.state.stores[i + this.state.stoppped];
                }
                console.log(showIt);
                this.setState({ stoppped: this.state.stoppped + 4, shown: showIt });
            }
        });
    }
    handelClick = () => {
        if (this.state.length >= 3) {
            var showIt = [];
            var i;
            for (i = 0; i < 4; i++) {
                console.log(i);
                showIt[i] = this.state.stores[i + this.state.stoppped];
                i++;
            }
            console.log(showIt);
            this.setState({ stoppped: this.state.stoppped + 4, shown: showIt });
        }
    }
    render() {
        return (
            <div>
                <h3 style={{ backgroundColor: "#00b5ad", color: "white", textAlign: "left", paddingLeft: "1%" }}>Customers</h3>
                <div onClick={this.handelClick}>see more</div>
                {/* <div>
                    {this.state.shown.map((x, i) => {
                        return <Store name={x.Name} url={x.Url} key={i}></Store>
                    })}
                </div> */}
            </div>
        )
    }
}
export default MyStores;
