import React, { Component } from 'react'
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Icon, Header, Image, Segment, FormInput, Input, TextArea, Label, Divider } from 'semantic-ui-react';
class ContactUs extends Component {
    state = {
        form: {
            name: {
                text: "Name",
                value: "",
                maxLength: 40,
                validition: {
                    required: true,
                    pattern: /^[a-zA-Z ]*$/
                },
                error: "The name is incorrect",
                valid: false,
            },
            mail: {
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validition: {
                    required: true,
                    pattern: /^/
                    // pattern: /^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/,
                },
                error: "The email is incorrect",
                valid: false,
            },
            phone: {
                text: "Phone",
                value: "",
                maxLength: 10,
                validition: {
                    required: true,
                    pattern: /^[0-9]*$/
                },
                error: "The phone is incorrect",
                valid: false,
            },
        },
        checkbox: false,
        trySend: false,
        loading: false,
        errorMessage: "",
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div style={{ backgroundColor: "#282c34", height: '60vh',minHeight:"60vh" }}>
            <h1 style={{ textAlign: "left", borderBottom: "solid black", fontSize: "16px", paddingLeft: "1%" }}>Contact Us</h1>

            <Grid textAlign='center' celled='internally' verticalAlign='middle'>
                <Grid.Column width={11} style={{}}>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Form.Group>
                            {array.map((x, i) => {
                                return <Form.Input
                                    width={5}
                                    key={i}
                                    placeholder={x.config.text}
                                    maxLength={x.config.maxLength}
                                    type="text"
                                    onChange={event => this.onChangeHendler(event, x.id)}
                                    className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                                    value={this.state.form[x.id].value}>
                                </Form.Input>
                            })};
                        </Form.Group>
                        <Form.Group>
                            <Form.TextArea placeholder="Description" width={15}></Form.TextArea>
                        </Form.Group>
                        <div id="message" style={{ color: "red" }}></div>
                        <Form.Button width={14} type="submit" color='teal' >Contact Me</Form.Button>
                    </Form>
                </Grid.Column>
                <Grid.Column width={4} style={{ paddingBottom: "8%", paddingTop: "8%", textAlign: "left",width:240,minWidth:240}}  >
                    <Label style={{ display: "block", padding: "3%", backgroundColor: "transparent",color:"white",fontSize:"100%",width:240,minWidth:240  }}><Icon name="envelope"></Icon>Email:easyclub100@gmail.com</Label>
                    <Label style={{ display: "block", padding: "3%", backgroundColor: "transparent",color:"white",width:240,minWidth:240  }}><Icon name="fax"></Icon>Fax:03-9258478</Label>
                    <Label style={{ display: "block", padding: "3%", backgroundColor: "transparent",color:"white",width:240,minWidth:240  }}><Icon name="phone"></Icon>Phone:0527689412</Label>
                </Grid.Column>
            </Grid>

        </div>

        )
    }
}
export default ContactUs
