import React, { Component } from 'react';
import ReactCardFlip from 'react-card-flip';
import { Button, Segment, Card, Image } from 'semantic-ui-react';
class CardClip extends Component {
    state = {
        isFlipped: false
    };
    handleClick = (e) => {
        e.preventDefault();
        this.setState({ isFlipped: !this.state.isFlipped });
    }
    render() {
        return (
            <ReactCardFlip isFlipped={this.state.isFlipped} flipDirection="vertical">
                <Card onMouseOver={this.handleClick} style={{ height: "12vh" }}>
                    
                    <Card.Content><Image src='https://react.semantic-ui.com/images/avatar/large/daniel.jpg' wrapped ui={false} />
                        {/* <Card.Header>Matthew Harris</Card.Header>
                        <Card.Meta>Co-Worker</Card.Meta>
                        <Card.Description>
                            Matthew is a pianist living in Nashville.
        </Card.Description> */}
                    </Card.Content>
                </Card>

                <Card onMouseOver={this.handleClick} style={{ height: "12vh" }}>
                    <Card.Content>
                        <Card.Header>Matthew Harris</Card.Header>
                        <Card.Meta>Co-Worker</Card.Meta>
                        <Card.Description>
                            Matthew is a pianist living in Nashville.
        </Card.Description>
                    </Card.Content>
                </Card>
            </ReactCardFlip>
        )
    }
}
export default CardClip;
