import * as actionName from './actionTypes'
const initalState={
    enterprise:{},
    loading:false,
    error:"",
}
const rootReducer=(state=initalState,action)=>{
    switch(action.type){
        case actionName.selectEnterprise:
            return{
                ...state,
                enterprise:action.payload      
            }
     
     default: return state
    }
}
export default rootReducer;
