import React, { Component } from 'react';
import 'semantic-ui-css/semantic.min.css';
import { Button, Form, Grid, Header, Image, Segment, Dimmer, Loader } from 'semantic-ui-react';
import axios from 'axios';
import validForm from '../../Service/ValidStateForm'
class UsersSignUp extends Component {
    state = {
        form: {
            Name: {
                type: "text",
                icon: "user",
                text: "Name",
                value: "",
                maxLength: 40,
                validation: {
                    required: true,
                    pattern: /^[a-zA-Z&.@ ]*$/,
                },
                valid: false,
            },
            Tz: {
                type: "text",
                icon: "tag",
                text: "Identity",
                value: "",
                maxLength: 9,
                validation: {
                    required: true,
                    pattern: /^[0-9]*$/,
                    tz: true
                },
                valid: false,
            },
            Email: {
                type: "text",
                icon: "envelope",
                text: "E-mail address",
                value: "",
                maxLength: 50,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z.+*%#!@]+$/,
                    mail: true
                },
                valid: false,
            },
            Password: {
                type: "password",
                icon: "lock",
                text: "Password",
                value: "",
                maxLength: 15,
                validation: {
                    required: true,
                    pattern: /^[0-9a-zA-Z]+$/,
                    password: true
                },
                valid: false,
            },
            Phone: {
                type: "text",
                icon: "phone",
                text: "Phone",
                value: "",
                maxLength: 10,
                validation: {
                    required: true,
                    pattern: /^[0-9]*$/,
                    phone: true
                },
                valid: false,
            },
            BirthDate: {
                birthdate: true,
                type: "text",
                icon: "birthday cake",
                text: "BirthDate",
                value: "",
                maxLength: 10,
                validation: {
                    required: true,
                    pattern: /^/
                },
                valid: false,
            },
        },
        validForm: false,
        trySend: false,
        loading: false,
        errorMessage:""
    }
    onChangeHendler = (e, id) => {
        this.setState({ trySend: false })
        const form = { ...this.state.form };
        const inputChanged = { ...form[id] };
        const value = inputChanged.value;
        if (!inputChanged.validation["pattern"].test(e.target.value)) {
            console.log(inputChanged.value)
            e.target.value = inputChanged.value
        }
        else
            inputChanged.value = e.target.value;
        console.log(inputChanged)
        form[id] = inputChanged;
        this.setState({ form: form })
    }
    SignUp = () => {
        const form = { ...this.state.form };
        var obj = validForm(form);
        for (let x in form) {
            const inputError = { ...form[x] };
            inputError.valid = true;
            form[x] = inputError;
        }
        if (obj !== null) {
            console.log(obj)
            const inputError = { ...form[obj.id] };
            inputError.valid = false;
            form[obj.id] = inputError;
            // form.error = obj.error;
            document.getElementById("message").innerHTML = obj.error;
            this.setState({ form: form, trySend: true })
        }
        else {
            this.setState({ loading: true });
            const data = {};
            for (let x in this.state.form) {
                data[x] = this.state.form[x].value;
            }
            axios.post(`http://localhost:64430/api/Users`, data
            ).then(x => {
                this.props.history.replace('/users/login');
            }
            ).catch(x => {
                this.setState({ loading: false });
                document.getElementById("message").innerHTML = x.response.data.Message;
            });
        }
    }
    render() {
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        console.log(array)
        return (<div>
            <Grid textAlign='center' style={{ height: '100vh' }} verticalAlign='middle'>
                <Grid.Column style={{ maxWidth: 450 }}>
                    <Header as='h2' color='teal' textAlign='center'>
                        <Image src='/Images/logo.png' /> Sign-Up to your account
                  </Header>
                    <Form size='large' onSubmit={this.SignUp}>
                        <Segment stacked>
                            {array.map((x, i) => {
                                console.log(x.config)
                                return <Form.Input
                                    key={i} iconPosition="left"
                                    maxLength={x.config.maxLength}
                                    type={x.config.type}
                                    onChange={event => this.onChangeHendler(event, x.id)}
                                    className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                                    placeholder={x.config.text + "*"}
                                    icon={x.config.icon}
                                    onFocus={(e) => x.config.birthdate === true ? e.target.type = 'date' : "text"}
                                    onKeyPress={x.id == "BirthDate" ? (e) => { e.preventDefault() } : ""}
                                    max={(new Date(new Date().getFullYear() - 18, new Date().getMonth(), new Date().getDay()).toISOString().split('T')[0])}
                                    min={(new Date(new Date().getFullYear() - 100, new Date().getMonth(), new Date().getDay()).toISOString().split('T')[0])}
                                >
                                </Form.Input>
                            })}
                            <div id="message" style={{ color: "red" }}></div>
                            <Button type="submit" color='teal' fluid size='large'>
                                Sign-Up
                            </Button>
                        </Segment>
                    </Form>
                </Grid.Column>
            </Grid>
            <Dimmer active={this.state.loading} inverted>
                <Loader size='large'>Loading</Loader>
            </Dimmer>
        </div >
        )

    }
}
export default UsersSignUp;
