import React, { Component } from 'react';
import Lotery from '../Lotery/Lotery';
import { Button, Form, Grid, Header, Image, Message, Segment, Checkbox, Select, Input, Popup } from 'semantic-ui-react';
import { NavLink, withRouter } from 'react-router-dom';
import { InputFile } from 'semantic-ui-react-input-file';
import { Route, Switch, Redirect, Router } from 'react-router-dom';
import { connect } from 'react-redux'
import { selectCard } from "../../../Store/actions/EnterpriseAction"
import axios from 'axios';
// import convertImg from "../../../convertImg"
class NewCard extends Component {

    state = {
        form: {
            cost: {
                icon: 'dollar sign',
                text: 'Card Price',
                type: 'text',
                maxLength: 7,
            },
        },
        type: "",
        img: null,
        fileName: "",
    }
    hanleSelctOnChange = (event, { value }) => {
        console.log(value);
        //להמיר לסטרינג
        console.log("ghhj")
        this.setState({ type: value })
        console.log(this.state.type)


    }
    click = () => {
    }
    convertImg = (event) => {
        const file = event.target.files[0];
        var name = file.name;
        const reader = new FileReader();
        reader.readAsDataURL(file);
        console.log(reader)
        reader.onload = () => {
            this.setState({ fileName: name })
            this.setState({ img: String(reader.result) })
            // this.setState({fileName:String(reader.fileName)})
        }
    }
    render() {
        console.log(this.state.fileName)
        console.log(this.state.img)
        const array = [];
        for (let x in this.state.form) {
            array.push({ id: x, config: this.state.form[x] })
        }
        return (<div style={{ marginLeft: "1vw" }}>
            <h1 style={{ width: '30vw', marginLeft: '35vw', marginTop: '5vh', textAlign: 'center', fontSize: '180%', color: 'rgb(40, 44, 52)' }}>New Card</h1>
            {/* <Grid textAlign='center' style={{ height: '75vh', marginTop: '10vh' }} verticalAlign='middle'> */}
            {/* <Grid.Column style={{ maxWidth: 450, boxShadow: '5px 2px 1px -1px rgba(0, 0, 0, 0.2), 5px 5px 5px 5px rgba(0, 0, 0, 0.14), 5px 1px 7px 4px rgba(0, 0, 0, 0.12)', borderRadius: '10%', height: '67%', width: '37vw' }}> */}
            <Header as='h2' color='teal' textAlign='left'>Card Details</Header>
            <Form onSubmit={this.onSubmitHandler}>
                {/* <Segment stacked> */}
                {array.map((x, i) => {
                    console.log(x.config)
                    return <Form.Input>
                        <Input>
                        label="Price For Customer"
                        labelPosition="left"
                        key={i} iconPosition="left"
                        maxLength={x.config.maxLength}
                        type={x.config.type}
                        // onChange={event => this.onChangeHendler(event, x.id)}
                        // className={this.state.trySend && !this.state.form[x.id].valid ? "error" : x.id}
                        placeholder={x.config.text + "*"}
                        icon={x.config.icon}
                        step={x.config.step}
                        checked={x.config.checked}
                    // onFocus={(e) => x.config.birthdate===true?e.target.type = 'date':"text"} 
                    </Input>
                    </Form.Input>
                })}
                <Form.Input label="Count Points">
                    <Popup
                        content="For example 5 point =1$"
                        trigger={
                            <Input type="number" label=' = 1 $' labelPosition="right" placeholder='Count Points' />
                        }
                    />
                  </Form.Input> 
                <Form.Select
                label="Card Type"
                    placeholder="Type"
                    options={[{ key: "Basic", text: 'Basic card', value: 'Basic card' }, { key: "Primium", text: 'Primium card', value: 'Primium card' }]}
                    onChange={this.hanleSelctOnChange}

                ></Form.Select>
                <Form.Input label="Enterprise Image">
                <InputFile class=".ui.form .field .ui.input"
                label="Image"
                    button={{
                        content: this.state.fileName,
                    }}
                    input={{
                        id: 'input-control-id',
                        onChange: this.convertImg,
                        name: this.state.fileName,
                    }}
                /></Form.Input>
                <Button type="submit" color='teal' style={{ fontSize: "18px", marginTop: "9vh", padding: "2vh", width: "25%", backgroundColor: "#282c34" }}>Next</Button>

                {/* </Segment> */}
            </Form>
            {/* </Grid.Column>
            </Grid> */}
        </div>
        )
    }
}
// const mapStateToProps = state => {
//     return {
//       enterprise: state.enterprise.enterprise
//     }
//   }
const mapDispatchToProps = dispatch => {
    return {
        selectCard: (card) => dispatch(selectCard(card)),
    }
}
export default connect(null, mapDispatchToProps)(NewCard);
